     1→package strategy
     2→
     3→import (
     4→	"context"
     5→	"errors"
     6→	"fmt"
     7→	"os"
     8→	"os/exec"
     9→	"path/filepath"
    10→	"strings"
    11→
    12→	"github.com/entireio/cli/cmd/entire/cli/settings"
    13→)
    14→
    15→// Hook marker used to identify Entire CLI hooks
    16→const entireHookMarker = "Entire CLI hooks"
    17→
    18→const backupSuffix = ".pre-entire"
    19→const chainComment = "# Chain: run pre-existing hook"
    20→
    21→// gitHookNames are the git hooks managed by Entire CLI
    22→var gitHookNames = []string{"prepare-commit-msg", "commit-msg", "post-commit", "pre-push"}
    23→
    24→// hookSpec defines a git hook's name and content template (without chain call).
    25→type hookSpec struct {
    26→	name    string
    27→	content string
    28→}
    29→
    30→// GetGitDir returns the actual git directory path by delegating to git itself.
    31→// This handles both regular repositories and worktrees, and inherits git's
    32→// security validation for gitdir references.
    33→func GetGitDir() (string, error) {
    34→	return getGitDirInPath(".")
    35→}
    36→
    37→// getGitDirInPath returns the git directory for a repository at the given path.
    38→// It delegates to `git rev-parse --git-dir` to leverage git's own validation.
    39→func getGitDirInPath(dir string) (string, error) {
    40→	ctx := context.Background()
    41→	cmd := exec.CommandContext(ctx, "git", "rev-parse", "--git-dir")
    42→	cmd.Dir = dir
    43→	output, err := cmd.Output()
    44→	if err != nil {
    45→		return "", errors.New("not a git repository")
    46→	}
    47→
    48→	gitDir := strings.TrimSpace(string(output))
    49→
    50→	// git rev-parse --git-dir returns relative paths from the working directory,
    51→	// so we need to make it absolute if it isn't already
    52→	if !filepath.IsAbs(gitDir) {
    53→		gitDir = filepath.Join(dir, gitDir)
    54→	}
    55→
    56→	return filepath.Clean(gitDir), nil
    57→}
    58→
    59→// IsGitHookInstalled checks if all generic Entire CLI hooks are installed.
    60→func IsGitHookInstalled() bool {
    61→	gitDir, err := GetGitDir()
    62→	if err != nil {
    63→		return false
    64→	}
    65→	for _, hook := range gitHookNames {
    66→		hookPath := filepath.Join(gitDir, "hooks", hook)
    67→		data, err := os.ReadFile(hookPath) //nolint:gosec // Path is constructed from constants
    68→		if err != nil {
    69→			return false
    70→		}
    71→		if !strings.Contains(string(data), entireHookMarker) {
    72→			return false
    73→		}
    74→	}
    75→	return true
    76→}
    77→
    78→// buildHookSpecs returns the hook specifications for all managed hooks.
    79→func buildHookSpecs(cmdPrefix string) []hookSpec {
    80→	return []hookSpec{
    81→		{
    82→			name: "prepare-commit-msg",
    83→			content: fmt.Sprintf(`#!/bin/sh
    84→# %s
    85→%s hooks git prepare-commit-msg "$1" "$2" 2>/dev/null || true
    86→`, entireHookMarker, cmdPrefix),
    87→		},
    88→		{
    89→			name: "commit-msg",
    90→			content: fmt.Sprintf(`#!/bin/sh
    91→# %s
    92→# Commit-msg hook: strip trailer if no user content (allows aborting empty commits)
    93→%s hooks git commit-msg "$1" || exit 1
    94→`, entireHookMarker, cmdPrefix),
    95→		},
    96→		{
    97→			name: "post-commit",
    98→			content: fmt.Sprintf(`#!/bin/sh
    99→# %s
   100→# Post-commit hook: condense session data if commit has Entire-Checkpoint trailer
   101→%s hooks git post-commit 2>/dev/null || true
   102→`, entireHookMarker, cmdPrefix),
   103→		},
   104→		{
   105→			name: "pre-push",
   106→			content: fmt.Sprintf(`#!/bin/sh
   107→# %s
   108→# Pre-push hook: push session logs alongside user's push
   109→# $1 is the remote name (e.g., "origin")
   110→%s hooks git pre-push "$1" || true
   111→`, entireHookMarker, cmdPrefix),
   112→		},
   113→	}
   114→}
   115→
   116→// InstallGitHook installs generic git hooks that delegate to `entire hook` commands.
   117→// These hooks work with any strategy - the strategy is determined at runtime.
   118→// If silent is true, no output is printed (except backup notifications, which always print).
   119→// Returns the number of hooks that were installed (0 if all already up to date).
   120→func InstallGitHook(silent bool) (int, error) {
   121→	gitDir, err := GetGitDir()
   122→	if err != nil {
   123→		return 0, err
   124→	}
   125→
   126→	hooksDir := filepath.Join(gitDir, "hooks")
   127→	if err := os.MkdirAll(hooksDir, 0o755); err != nil { //nolint:gosec // Git hooks require executable permissions
   128→		return 0, fmt.Errorf("failed to create hooks directory: %w", err)
   129→	}
   130→
   131→	// Determine command prefix based on local_dev setting
   132→	var cmdPrefix string
   133→	if isLocalDev() {
   134→		cmdPrefix = "go run ./cmd/entire/main.go"
   135→	} else {
   136→		cmdPrefix = "entire"
   137→	}
   138→
   139→	specs := buildHookSpecs(cmdPrefix)
   140→	installedCount := 0
   141→
   142→	for _, spec := range specs {
   143→		hookPath := filepath.Join(hooksDir, spec.name)
   144→		backupPath := hookPath + backupSuffix
   145→		backupExists := fileExists(backupPath)
   146→
   147→		// Back up existing non-Entire hooks
   148→		existing, existingErr := os.ReadFile(hookPath) //nolint:gosec // path is controlled
   149→		if existingErr == nil && !strings.Contains(string(existing), entireHookMarker) {
   150→			if !backupExists {
   151→				if err := os.Rename(hookPath, backupPath); err != nil {
   152→					return installedCount, fmt.Errorf("failed to back up %s: %w", spec.name, err)
   153→				}
   154→				fmt.Fprintf(os.Stderr, "[entire] Backed up existing %s to %s%s\n", spec.name, spec.name, backupSuffix)
   155→			} else {
   156→				fmt.Fprintf(os.Stderr, "[entire] Warning: replacing %s (backup %s%s already exists from a previous install)\n", spec.name, spec.name, backupSuffix)
   157→			}
   158→			backupExists = true
   159→		}
   160→
   161→		// Chain to backup if one exists
   162→		content := spec.content
   163→		if backupExists {
   164→			content = generateChainedContent(spec.content, spec.name)
   165→		}
   166→
   167→		written, err := writeHookFile(hookPath, content)
   168→		if err != nil {
   169→			return installedCount, fmt.Errorf("failed to install %s hook: %w", spec.name, err)
   170→		}
   171→		if written {
   172→			installedCount++
   173→		}
   174→	}
   175→
   176→	if !silent {
   177→		fmt.Println("✓ Installed git hooks (prepare-commit-msg, commit-msg, post-commit, pre-push)")
   178→		fmt.Println("  Hooks delegate to the current strategy at runtime")
   179→	}
   180→
   181→	return installedCount, nil
   182→}
   183→
   184→// writeHookFile writes a hook file if it doesn't exist or has different content.
   185→// Returns true if the file was written, false if it already had the same content.
   186→func writeHookFile(path, content string) (bool, error) {
   187→	// Check if file already exists with same content
   188→	existing, err := os.ReadFile(path) //nolint:gosec // path is controlled
   189→	if err == nil && string(existing) == content {
   190→		return false, nil // Already up to date
   191→	}
   192→
   193→	// Git hooks must be executable (0o755)
   194→	if err := os.WriteFile(path, []byte(content), 0o755); err != nil { //nolint:gosec // Git hooks require executable permissions
   195→		return false, fmt.Errorf("failed to write hook file %s: %w", path, err)
   196→	}
   197→	return true, nil
   198→}
   199→
   200→// RemoveGitHook removes all Entire CLI git hooks from the repository.
   201→// If a .pre-entire backup exists, it is restored.
   202→// Returns the number of hooks removed.
   203→func RemoveGitHook() (int, error) {
   204→	gitDir, err := GetGitDir()
   205→	if err != nil {
   206→		return 0, err
   207→	}
   208→
   209→	hooksDir := filepath.Join(gitDir, "hooks")
   210→	removed := 0
   211→	var removeErrors []string
   212→
   213→	for _, hook := range gitHookNames {
   214→		hookPath := filepath.Join(hooksDir, hook)
   215→		backupPath := hookPath + backupSuffix
   216→
   217→		// Remove the hook if it contains our marker
   218→		data, err := os.ReadFile(hookPath) //nolint:gosec // path is controlled
   219→		hookIsOurs := err == nil && strings.Contains(string(data), entireHookMarker)
   220→		hookExists := err == nil
   221→
   222→		if hookIsOurs {
   223→			if err := os.Remove(hookPath); err != nil {
   224→				removeErrors = append(removeErrors, fmt.Sprintf("%s: %v", hook, err))
   225→				continue
   226→			}
   227→			removed++
   228→		}
   229→
   230→		// Restore .pre-entire backup if it exists
   231→		if fileExists(backupPath) {
   232→			if hookExists && !hookIsOurs {
   233→				// A non-Entire hook is present — don't overwrite it with the backup
   234→				fmt.Fprintf(os.Stderr, "[entire] Warning: %s was modified since install; backup %s%s left in place\n", hook, hook, backupSuffix)
   235→			} else {
   236→				if err := os.Rename(backupPath, hookPath); err != nil {
   237→					removeErrors = append(removeErrors, fmt.Sprintf("restore %s%s: %v", hook, backupSuffix, err))
   238→				}
   239→			}
   240→		}
   241→	}
   242→
   243→	if len(removeErrors) > 0 {
   244→		return removed, fmt.Errorf("failed to remove hooks: %s", strings.Join(removeErrors, "; "))
   245→	}
   246→	return removed, nil
   247→}
   248→
   249→// generateChainedContent appends a chain call to the base hook content,
   250→// so the pre-existing hook (backed up to .pre-entire) is called after our hook.
   251→func generateChainedContent(baseContent, hookName string) string {
   252→	return baseContent + fmt.Sprintf(`%s
   253→_entire_hook_dir="$(dirname "$0")"
   254→if [ -x "$_entire_hook_dir/%s%s" ]; then
