    20→)
    21→
    22→func newStatusCmd() *cobra.Command {
    23→	var detailed bool
    24→
    25→	cmd := &cobra.Command{
    26→		Use:   "status",
    27→		Short: "Show Entire status",
    28→		Long:  "Show whether Entire is currently enabled or disabled",
    29→		RunE: func(cmd *cobra.Command, _ []string) error {
    30→			return runStatus(cmd.OutOrStdout(), detailed)
    31→		},
    32→	}
    33→
    34→	cmd.Flags().BoolVar(&detailed, "detailed", false, "Show detailed status for each settings file")
    35→
    36→	return cmd
    37→}
    38→
    39→func runStatus(w io.Writer, detailed bool) error {
    40→	// Check if we're in a git repository
    41→	if _, repoErr := paths.RepoRoot(); repoErr != nil {
    42→		fmt.Fprintln(w, "✕ not a git repository")
    43→		return nil //nolint:nilerr // Not being in a git repo is a valid status, not an error
    44→	}
    45→
    46→	// Get absolute paths for settings files
    47→	settingsPath, err := paths.AbsPath(EntireSettingsFile)
    48→	if err != nil {
    49→		settingsPath = EntireSettingsFile
    50→	}
    51→	localSettingsPath, err := paths.AbsPath(EntireSettingsLocalFile)
    52→	if err != nil {
    53→		localSettingsPath = EntireSettingsLocalFile
    54→	}
    55→
    56→	// Check which settings files exist
    57→	_, projectErr := os.Stat(settingsPath)
    58→	if projectErr != nil && !errors.Is(projectErr, fs.ErrNotExist) {
    59→		return fmt.Errorf("cannot access project settings file: %w", projectErr)
    60→	}
    61→	_, localErr := os.Stat(localSettingsPath)
    62→	if localErr != nil && !errors.Is(localErr, fs.ErrNotExist) {
    63→		return fmt.Errorf("cannot access local settings file: %w", localErr)
    64→	}
    65→	projectExists := projectErr == nil
    66→	localExists := localErr == nil
    67→
    68→	if !projectExists && !localExists {
    69→		fmt.Fprintln(w, "○ not set up (run `entire enable` to get started)")
    70→		return nil
    71→	}
    72→
    73→	if detailed {
    74→		return runStatusDetailed(w, settingsPath, localSettingsPath, projectExists, localExists)
    75→	}
    76→
    77→	// Short output: just show the effective/merged state
    78→	settings, err := LoadEntireSettings()
    79→	if err != nil {
    80→		return fmt.Errorf("failed to load settings: %w", err)
    81→	}
    82→
    83→	fmt.Fprintln(w, formatSettingsStatusShort(settings))
    84→
    85→	if settings.Enabled {
    86→		writeActiveSessions(w)
    87→	}
    88→
    89→	return nil
    90→}
    91→
    92→// runStatusDetailed shows the effective status plus detailed status for each settings file.
    93→func runStatusDetailed(w io.Writer, settingsPath, localSettingsPath string, projectExists, localExists bool) error {
    94→	// First show the effective/merged status
    95→	settings, err := LoadEntireSettings()
    96→	if err != nil {
    97→		return fmt.Errorf("failed to load settings: %w", err)
    98→	}
    99→	fmt.Fprintln(w, formatSettingsStatusShort(settings))
   100→	fmt.Fprintln(w) // blank line
   101→
   102→	// Show project settings if it exists
   103→	if projectExists {
   104→		projectSettings, err := loadSettingsFromFile(settingsPath)
   105→		if err != nil {
   106→			return fmt.Errorf("failed to load project settings: %w", err)
   107→		}
   108→		fmt.Fprintln(w, formatSettingsStatus("Project", projectSettings))
   109→	}
   110→
   111→	// Show local settings if it exists
   112→	if localExists {
   113→		localSettings, err := loadSettingsFromFile(localSettingsPath)
   114→		if err != nil {
   115→			return fmt.Errorf("failed to load local settings: %w", err)
   116→		}
   117→		fmt.Fprintln(w, formatSettingsStatus("Local", localSettings))
   118→	}
   119→
   120→	if settings.Enabled {
   121→		writeActiveSessions(w)
   122→	}
   123→
   124→	return nil
   125→}
   126→
   127→// formatSettingsStatusShort formats a short settings status line.
   128→// Output format: "Enabled (manual-commit)" or "Disabled (auto-commit)"
   129→func formatSettingsStatusShort(settings *EntireSettings) string {
   130→	displayName := settings.Strategy
   131→	if dn, ok := strategyInternalToDisplay[settings.Strategy]; ok {
   132→		displayName = dn
   133→	}
   134→
   135→	if settings.Enabled {
   136→		return fmt.Sprintf("Enabled (%s)", displayName)
   137→	}
   138→	return fmt.Sprintf("Disabled (%s)", displayName)
   139→}
   140→
   141→// formatSettingsStatus formats a settings status line with source prefix.
   142→// Output format: "Project, enabled (manual-commit)" or "Local, disabled (auto-commit)"
   143→func formatSettingsStatus(prefix string, settings *EntireSettings) string {
   144→	displayName := settings.Strategy
   145→	if dn, ok := strategyInternalToDisplay[settings.Strategy]; ok {
   146→		displayName = dn
   147→	}
   148→
   149→	if settings.Enabled {
   150→		return fmt.Sprintf("%s, enabled (%s)", prefix, displayName)
   151→	}
   152→	return fmt.Sprintf("%s, disabled (%s)", prefix, displayName)
   153→}
   154→
   155→// timeAgo formats a time as a human-readable relative duration.
   156→func timeAgo(t time.Time) string {
   157→	d := time.Since(t)
   158→	switch {
   159→	case d < time.Minute:
   160→		return "just now"
   161→	case d < time.Hour:
   162→		m := int(d.Minutes())
   163→		return fmt.Sprintf("%dm ago", m)
   164→	case d < 24*time.Hour:
   165→		h := int(d.Hours())
   166→		return fmt.Sprintf("%dh ago", h)
   167→	default:
   168→		days := int(d.Hours() / 24)
   169→		return fmt.Sprintf("%dd ago", days)
   170→	}
   171→}
   172→
   173→// worktreeGroup groups sessions by worktree path for display.
   174→type worktreeGroup struct {
   175→	path     string
   176→	branch   string
   177→	sessions []*session.State
   178→}
   179→
   180→const unknownPlaceholder = "(unknown)"
   181→
   182→// writeActiveSessions writes active session information grouped by worktree.
   183→func writeActiveSessions(w io.Writer) {
   184→	store, err := session.NewStateStore()
   185→	if err != nil {
   186→		return
   187→	}
   188→
   189→	states, err := store.List(context.Background())
   190→	if err != nil || len(states) == 0 {
   191→		return
   192→	}
   193→
   194→	// Filter to active sessions only
   195→	var active []*session.State
   196→	for _, s := range states {
   197→		if s.EndedAt == nil {
   198→			active = append(active, s)
   199→		}
   200→	}
   201→	if len(active) == 0 {
   202→		return
   203→	}
   204→
   205→	// Group by worktree path
   206→	groups := make(map[string]*worktreeGroup)
   207→	for _, s := range active {
   208→		wp := s.WorktreePath
   209→		if wp == "" {
   210→			wp = unknownPlaceholder
   211→		}
   212→		g, ok := groups[wp]
   213→		if !ok {
   214→			g = &worktreeGroup{path: wp}
   215→			groups[wp] = g
   216→		}
   217→		g.sessions = append(g.sessions, s)
   218→	}
   219→
   220→	// Resolve branch names for each worktree (skip for unknown paths)
   221→	for _, g := range groups {
   222→		if g.path != unknownPlaceholder {
   223→			g.branch = resolveWorktreeBranch(g.path)
   224→		}
   225→	}
   226→
   227→	// Sort groups: alphabetical by path
   228→	sortedGroups := make([]*worktreeGroup, 0, len(groups))
   229→	for _, g := range groups {
