    29→// setupTestRepo creates a temp directory with a git repo initialized.
    30→func setupTestRepo(t *testing.T) {
    31→	t.Helper()
    32→	tmpDir := setupTestDir(t)
    33→	if _, err := git.PlainInit(tmpDir, false); err != nil {
    34→		t.Fatalf("Failed to init repo: %v", err)
    35→	}
    36→}
