   168→func TestRunDisable_WithLocalSettings(t *testing.T) {
   169→	setupTestDir(t)
   170→	// Create both settings files with enabled: true
   171→	writeSettings(t, testSettingsEnabled)
   172→	writeLocalSettings(t, `{"enabled": true}`)
   173→
   174→	var stdout bytes.Buffer
   175→	if err := runDisable(&stdout, false); err != nil {
   176→		t.Fatalf("runDisable() error = %v", err)
   177→	}
   178→
   179→	// Should be disabled because runDisable updates local settings when it exists
   180→	enabled, err := IsEnabled()
   181→	if err != nil {
   182→		t.Fatalf("IsEnabled() error = %v", err)
   183→	}
   184→	if enabled {
   185→		t.Error("Entire should be disabled after running disable command (local settings should be updated)")
   186→	}
   187→
   188→	// Verify local settings file was updated
   189→	localContent, err := os.ReadFile(EntireSettingsLocalFile)
   190→	if err != nil {
   191→		t.Fatalf("Failed to read local settings: %v", err)
   192→	}
   193→	if !strings.Contains(string(localContent), `"enabled":false`) && !strings.Contains(string(localContent), `"enabled": false`) {
   194→		t.Errorf("Local settings should have enabled:false, got: %s", localContent)
   195→	}
   196→}
