   229→// TestRunDisable_CreatesLocalSettingsWhenMissing verifies that running
   230→// `entire disable` without --project creates settings.local.json when it
   231→// doesn't exist, rather than writing to settings.json.
   232→func TestRunDisable_CreatesLocalSettingsWhenMissing(t *testing.T) {
   233→	setupTestDir(t)
   234→	// Only create project settings (no local settings)
   235→	writeSettings(t, testSettingsEnabled)
   236→
   237→	var stdout bytes.Buffer
   238→	if err := runDisable(&stdout, false); err != nil {
   239→		t.Fatalf("runDisable() error = %v", err)
   240→	}
   241→
   242→	// Should be disabled
   243→	enabled, err := IsEnabled()
   244→	if err != nil {
   245→		t.Fatalf("IsEnabled() error = %v", err)
   246→	}
   247→	if enabled {
   248→		t.Error("Entire should be disabled after running disable command")
   249→	}
   250→
   251→	// Local settings file should be created with enabled:false
   252→	localContent, err := os.ReadFile(EntireSettingsLocalFile)
   253→	if err != nil {
   254→		t.Fatalf("Local settings file should have been created: %v", err)
   255→	}
   256→	if !strings.Contains(string(localContent), `"enabled":false`) && !strings.Contains(string(localContent), `"enabled": false`) {
   257→		t.Errorf("Local settings should have enabled:false, got: %s", localContent)
   258→	}
   259→
   260→	// Project settings should remain unchanged (still enabled)
   261→	projectContent, err := os.ReadFile(EntireSettingsFile)
   262→	if err != nil {
   263→		t.Fatalf("Failed to read project settings: %v", err)
   264→	}
   265→	if !strings.Contains(string(projectContent), `"enabled":true`) && !strings.Contains(string(projectContent), `"enabled": true`) {
   266→		t.Errorf("Project settings should still have enabled:true, got: %s", projectContent)
   267→	}
   268→}
