   342→func TestRunEnableWithStrategy_PreservesExistingSettings(t *testing.T) {
   343→	setupTestRepo(t)
   344→
   345→	// Create initial settings with strategy_options (like push enabled)
   346→	initialSettings := `{
   347→		"strategy": "manual-commit",
   348→		"enabled": true,
   349→		"strategy_options": {
   350→			"push": true,
   351→			"some_other_option": "value"
   352→		}
   353→	}`
   354→	writeSettings(t, initialSettings)
   355→
   356→	// Run enable with a different strategy
   357→	var stdout bytes.Buffer
   358→	err := runEnableWithStrategy(&stdout, "auto-commit", false, false, false, true, false, false, false)
   359→	if err != nil {
   360→		t.Fatalf("runEnableWithStrategy() error = %v", err)
   361→	}
   362→
   363→	// Load the saved settings and verify strategy_options were preserved
   364→	settings, err := LoadEntireSettings()
   365→	if err != nil {
   366→		t.Fatalf("LoadEntireSettings() error = %v", err)
   367→	}
   368→
   369→	// Strategy should be updated
   370→	if settings.Strategy != "auto-commit" {
   371→		t.Errorf("Strategy should be 'auto-commit', got %q", settings.Strategy)
   372→	}
   373→
   374→	// strategy_options should be preserved
   375→	if settings.StrategyOptions == nil {
   376→		t.Fatal("strategy_options should be preserved, but got nil")
   377→	}
   378→	if settings.StrategyOptions["push"] != true {
   379→		t.Errorf("strategy_options.push should be true, got %v", settings.StrategyOptions["push"])
   380→	}
   381→	if settings.StrategyOptions["some_other_option"] != "value" {
   382→		t.Errorf("strategy_options.some_other_option should be 'value', got %v", settings.StrategyOptions["some_other_option"])
   383→	}
   384→}
