   589→func TestShellCompletionTarget(t *testing.T) {
   590→	tests := []struct {
   591→		name             string
   592→		shell            string
   593→		createBashProf   bool
   594→		wantShell        string
   595→		wantRCBase       string // basename of rc file
   596→		wantCompletion   string
   597→		wantErrUnsupport bool
   598→	}{
   599→		{
   600→			name:           "zsh",
   601→			shell:          "/bin/zsh",
   602→			wantShell:      "Zsh",
   603→			wantRCBase:     ".zshrc",
   604→			wantCompletion: "source <(entire completion zsh)",
   605→		},
   606→		{
   607→			name:           "bash_no_profile",
   608→			shell:          "/bin/bash",
   609→			wantShell:      "Bash",
   610→			wantRCBase:     ".bashrc",
   611→			wantCompletion: "source <(entire completion bash)",
   612→		},
   613→		{
   614→			name:           "bash_with_profile",
   615→			shell:          "/bin/bash",
   616→			createBashProf: true,
   617→			wantShell:      "Bash",
   618→			wantRCBase:     ".bash_profile",
   619→			wantCompletion: "source <(entire completion bash)",
   620→		},
   621→		{
   622→			name:           "fish",
   623→			shell:          "/usr/bin/fish",
   624→			wantShell:      "Fish",
   625→			wantRCBase:     filepath.Join(".config", "fish", "config.fish"),
   626→			wantCompletion: "entire completion fish | source",
   627→		},
   628→		{
   629→			name:             "empty_shell",
   630→			shell:            "",
   631→			wantErrUnsupport: true,
   632→		},
   633→	}
   634→
   635→	for _, tt := range tests {
   636→		t.Run(tt.name, func(t *testing.T) {
   637→			home := t.TempDir()
   638→			t.Setenv("HOME", home)
