   640→
   641→			if tt.createBashProf {
   642→				if err := os.WriteFile(filepath.Join(home, ".bash_profile"), []byte(""), 0o644); err != nil {
   643→					t.Fatal(err)
   644→				}
   645→			}
   646→
   647→			shellName, rcFile, completion, err := shellCompletionTarget()
   648→
   649→			if tt.wantErrUnsupport {
   650→				if err != errUnsupportedShell {
   651→					t.Fatalf("got err=%v, want errUnsupportedShell", err)
   652→				}
   653→				return
   654→			}
   655→			if err != nil {
   656→				t.Fatalf("unexpected error: %v", err)
   657→			}
   658→			if shellName != tt.wantShell {
   659→				t.Errorf("shellName = %q, want %q", shellName, tt.wantShell)
   660→			}
   661→			wantRC := filepath.Join(home, tt.wantRCBase)
   662→			if rcFile != wantRC {
   663→				t.Errorf("rcFile = %q, want %q", rcFile, wantRC)
   664→			}
   665→			if completion != tt.wantCompletion {
   666→				t.Errorf("completion = %q, want %q", completion, tt.wantCompletion)
   667→			}
   668→		})
   669→	}
   670→}
