	}

	// Stanza should be present
	version, _, found := FindStanza(content, stanzaName)
	if !found {
		t.Fatal("stanza should be present after migration")
	}
	if version != stanzaVersion {
		t.Errorf("stanza version = %d, want %d", version, stanzaVersion)
