func TestShellCompletionTarget(t *testing.T) {
	tests := []struct {
		name             string
		shell            string
		createBashProf   bool
		wantShell        string
		wantRCBase       string // basename of rc file
		wantCompletion   string
		wantErrUnsupport bool
	}{
		{
			name:           "zsh",
			shell:          "/bin/zsh",
			wantShell:      "Zsh",
			wantRCBase:     ".zshrc",
			wantCompletion: "source <(entire completion zsh)",
		},
		{
			name:           "bash_no_profile",
			shell:          "/bin/bash",
			wantShell:      "Bash",
			wantRCBase:     ".bashrc",
			wantCompletion: "source <(entire completion bash)",
		},
		{
			name:           "bash_with_profile",
			shell:          "/bin/bash",
			createBashProf: true,
			wantShell:      "Bash",
			wantRCBase:     ".bash_profile",
			wantCompletion: "source <(entire completion bash)",
		},
		{
			name:           "fish",
			shell:          "/usr/bin/fish",
			wantShell:      "Fish",
			wantRCBase:     filepath.Join(".config", "fish", "config.fish"),
			wantCompletion: "entire completion fish | source",
		},
		{
			name:             "empty_shell",
			shell:            "",
			wantErrUnsupport: true,
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			home := t.TempDir()
			t.Setenv("HOME", home)
			t.Setenv("SHELL", tt.shell)

			if tt.createBashProf {
				if err := os.WriteFile(filepath.Join(home, ".bash_profile"), []byte(""), 0o644); err != nil {
					t.Fatal(err)
				}
			}

			shellName, rcFile, completion, err := shellCompletionTarget()

			if tt.wantErrUnsupport {
				if err != errUnsupportedShell {
					t.Fatalf("got err=%v, want errUnsupportedShell", err)
				}
				return
			}
			if err != nil {
				t.Fatalf("unexpected error: %v", err)
			}
			if shellName != tt.wantShell {
				t.Errorf("shellName = %q, want %q", shellName, tt.wantShell)
			}
			wantRC := filepath.Join(home, tt.wantRCBase)
			if rcFile != wantRC {
				t.Errorf("rcFile = %q, want %q", rcFile, wantRC)
			}
			if completion != tt.wantCompletion {
				t.Errorf("completion = %q, want %q", completion, tt.wantCompletion)
			}
		})
	}
}

func TestInstallShellCompletion(t *testing.T) {
	tests := []struct {
		name          string
		rcFileRelPath string
		completion    string
		preExisting   string // existing content in rc file; empty means file doesn't exist
		createParent  bool   // whether parent dir already exists
		want          string
	}{
		{
			name:         "zsh_new_file",
			rcFileRelPath: ".zshrc",
			completion:   "source <(entire completion zsh)",
			createParent: true,
			want: `# BEGIN entire-cli (v1)
source <(entire completion zsh)
# END entire-cli
`,
		},
		{
			name:         "zsh_existing_file",
			rcFileRelPath: ".zshrc",
			completion:   "source <(entire completion zsh)",
			preExisting:  "# existing zshrc content\n",
			createParent: true,
			want: `# existing zshrc content

# BEGIN entire-cli (v1)
source <(entire completion zsh)
# END entire-cli
`,
		},
		{
			name:         "fish_no_parent_dir",
			rcFileRelPath: filepath.Join(".config", "fish", "config.fish"),
			completion:   "entire completion fish | source",
			createParent: false,
			want: `# BEGIN entire-cli (v1)
entire completion fish | source
# END entire-cli
`,
		},
		{
			name:         "fish_existing_dir",
			rcFileRelPath: filepath.Join(".config", "fish", "config.fish"),
			completion:   "entire completion fish | source",
			createParent: true,
			want: `# BEGIN entire-cli (v1)
entire completion fish | source
# END entire-cli
`,
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			home := t.TempDir()
			rcFile := filepath.Join(home, tt.rcFileRelPath)

			if tt.createParent {
				if err := os.MkdirAll(filepath.Dir(rcFile), 0o755); err != nil {
					t.Fatal(err)
				}
			}
			if tt.preExisting != "" {
				if err := os.WriteFile(rcFile, []byte(tt.preExisting), 0o644); err != nil {
					t.Fatal(err)
				}
			}

			if err := installShellCompletion(rcFile, tt.completion); err != nil {
				t.Fatalf("installShellCompletion() error: %v", err)
			}

			data, err := os.ReadFile(rcFile)
			if err != nil {
				t.Fatalf("reading rc file: %v", err)
			}
			if got := string(data); got != tt.want {
				t.Errorf("rc file content:\ngot:\n%s\nwant:\n%s", got, tt.want)
			}

			// Verify parent directory was created.
			info, err := os.Stat(filepath.Dir(rcFile))
			if err != nil {
				t.Fatalf("stat parent dir: %v", err)
			}
			if !info.IsDir() {
				t.Fatal("parent path is not a directory")
			}
		})
	}
}

func TestInstallShellCompletion_MigratesLegacy(t *testing.T) {
	home := t.TempDir()
	rcFile := filepath.Join(home, ".zshrc")
	legacy := `export PATH=/usr/bin

# Entire CLI shell completion
source <(entire completion zsh)
`
	if err := os.WriteFile(rcFile, []byte(legacy), 0o644); err != nil {
		t.Fatal(err)
	}

	if err := installShellCompletion(rcFile, "source <(entire completion zsh)"); err != nil {
		t.Fatalf("installShellCompletion() error: %v", err)
	}

	data, err := os.ReadFile(rcFile)
	if err != nil {
		t.Fatal(err)
	}
	content := string(data)

	// Legacy comment should be gone
	if strings.Contains(content, legacyCompletionComment) {
		t.Error("legacy comment should be removed after migration")
	}

	// Stanza should be present
	version, _, found := FindStanza(content, stanzaName)
	if !found {
		t.Fatal("stanza should be present after migration")
	}
	if version != stanzaVersion {
		t.Errorf("stanza version = %d, want %d", version, stanzaVersion)
	}

	// Pre-existing content preserved
	if !strings.Contains(content, "export PATH=/usr/bin") {
		t.Error("pre-existing content should be preserved")
	}
}

func TestCompletionStatus(t *testing.T) {
	home := t.TempDir()

	// Missing file
	rcFile := filepath.Join(home, ".zshrc")
	if status := completionStatus(rcFile); status != statusMissing {
		t.Errorf("missing file: got %d, want statusMissing", status)
	}

	// Empty file
	if err := os.WriteFile(rcFile, []byte(""), 0o644); err != nil {
		t.Fatal(err)
	}
	if status := completionStatus(rcFile); status != statusMissing {
		t.Errorf("empty file: got %d, want statusMissing", status)
	}

	// Legacy format
	legacy := `# Entire CLI shell completion
source <(entire completion zsh)
`
	if err := os.WriteFile(rcFile, []byte(legacy), 0o644); err != nil {
		t.Fatal(err)
	}
	if status := completionStatus(rcFile); status != statusLegacy {
		t.Errorf("legacy format: got %d, want statusLegacy", status)
	}

	// Current stanza
	current := `# BEGIN entire-cli (v1)
source <(entire completion zsh)
# END entire-cli
`
	if err := os.WriteFile(rcFile, []byte(current), 0o644); err != nil {
		t.Fatal(err)
	}
	if status := completionStatus(rcFile); status != statusCurrent {
		t.Errorf("current stanza: got %d, want statusCurrent", status)
	}

	// Stale stanza (older version)
	stale := `# BEGIN entire-cli (v0)
source <(entire completion zsh)
# END entire-cli
`
	if err := os.WriteFile(rcFile, []byte(stale), 0o644); err != nil {
		t.Fatal(err)
	}
	if status := completionStatus(rcFile); status != statusStale {
		t.Errorf("stale stanza: got %d, want statusStale", status)
	}
}

