if err := strat.SaveChanges(ctx); err != nil {
	return fmt.Errorf("failed to save changes: %w", err)
}

// Update session state with new transcript position for strategies that create
// commits on the active branch (auto-commit strategy). This prevents parsing old transcript
// lines on subsequent checkpoints.
// Note: Shadow strategy doesn't create commits on the active branch, so its
// checkpoints don't "consume" the transcript in the same way. Shadow should
// continue parsing the full transcript to capture all files touched in the session.
if strat.Name() == strategy.StrategyNameAutoCommit {
	// Load session state for updating transcript position
	sessionState, loadErr := strategy.LoadSessionState(sessionID)
	if loadErr != nil {
		fmt.Fprintf(os.Stderr, "Warning: failed to load session state: %v\n", loadErr)
	}
	// Create session state lazily if it doesn't exist (backward compat for resumed sessions
	// or if InitializeSession was never called/failed)
	if sessionState == nil {
		sessionState = &strategy.SessionState{
			SessionID: sessionID,
		}
	}
	sessionState.CheckpointTranscriptStart = totalLines
	sessionState.StepCount++

	// UserAddedPerFile tracks per-file user additions for accurate modification tracking.
	// This enables distinguishing user self-modifications from agent modifications.
	// See docs/architecture/attribution.md for details.
	UserAddedPerFile map[string]int `json:"user_added_per_file,omitempty"`
}

// NormalizeAfterLoad applies backward-compatible migrations to state loaded from disk.
// Call this after deserializing a State from JSON.
func (s *State) NormalizeAfterLoad() {
	// Migrate transcript fields: CheckpointTranscriptStart replaces CondensedTranscriptLines
	if s.CheckpointTranscriptStart == 0 && s.CondensedTranscriptLines > 0 {
		s.CheckpointTranscriptStart = s.CondensedTranscriptLines
	}
	// Clear deprecated fields so they aren't re-persisted
	s.CondensedTranscriptLines = 0
	s.TranscriptLinesAtStart = 0
}

// StateStore provides low-level operations for managing session state files.
//
// StateStore is a primitive for session state persistence. It is NOT the same as
// the Sessions interface - it only handles state files in .git/entire-sessions/,
// not the full session data which includes checkpoint content.
//
// Use StateStore directly in strategies for performance-critical state operations.
// Use the Sessions interface (when implemented) for high-level session management.
type StateStore struct {
	// stateDir is the directory where session state files are stored
	stateDir string
}

IncrementalData     []byte // Tool input payload for this checkpoint

// Commit message fields (used for task checkpoints)
CommitSubject string // Subject line for the metadata commit (overrides default)

// Agent identifies the agent that created this checkpoint (e.g., "Claude Code", "Cursor")
Agent agent.AgentType

// Transcript position at checkpoint start - tracks what was added during this checkpoint
TranscriptIdentifierAtStart string // Last identifier when checkpoint started (UUID for Claude, message ID for Gemini)
CheckpointTranscriptStart   int    // Transcript line offset at start of this checkpoint's data

// Deprecated: Use CheckpointTranscriptStart instead. Kept for backward compatibility.
TranscriptLinesAtStart int

// TokenUsage contains the token usage for this checkpoint
TokenUsage *agent.TokenUsage

// InitialAttribution is line-level attribution calculated at commit time
// comparing checkpoint tree (agent work) to committed tree (may include human edits)
InitialAttribution *InitialAttribution

// Summary is an optional AI-generated summary for this checkpoint.
// This field may be nil when:
//   - summarization is disabled in settings

// Token usage tracking (accumulated across all checkpoints in this session)
TokenUsage *agent.TokenUsage `json:"token_usage,omitempty"`

// Deprecated: TranscriptLinesAtStart is replaced by CheckpointTranscriptStart.
// Kept for backward compatibility with existing state files.
TranscriptLinesAtStart int `json:"transcript_lines_at_start,omitempty"`

// TranscriptIdentifierAtStart is the last transcript identifier when the session started.
// Used for identifier-based transcript scoping (UUID for Claude, message ID for Gemini).
TranscriptIdentifierAtStart string `json:"transcript_identifier_at_start,omitempty"`

// TranscriptPath is the path to the live transcript file (for mid-session commit detection)
TranscriptPath string `json:"transcript_path,omitempty"`

	Agent agent.AgentType `json:"agent,omitempty"`

	// Task checkpoint fields (only populated for task checkpoints)
	IsTask    bool   `json:"is_task,omitempty"`
	ToolUseID string `json:"tool_use_id,omitempty"`

	// Transcript position at checkpoint start - tracks what was added during this checkpoint
	TranscriptIdentifierAtStart string `json:"transcript_identifier_at_start,omitempty"` // Last identifier when checkpoint started (UUID for Claude, message ID for Gemini)
	CheckpointTranscriptStart   int    `json:"checkpoint_transcript_start,omitempty"`    // Transcript line offset at start of this checkpoint's data

	// Deprecated: Use CheckpointTranscriptStart instead. Written for backward compatibility with older CLI versions.
	TranscriptLinesAtStart int `json:"transcript_lines_at_start,omitempty"`

	// Token usage for this checkpoint
	TokenUsage *agent.TokenUsage `json:"token_usage,omitempty"`

	// AI-generated summary of the checkpoint
	Summary *Summary `json:"summary,omitempty"`

	// InitialAttribution is line-level attribution calculated at commit time
	InitialAttribution *InitialAttribution `json:"initial_attribution,omitempty"`
}

// GetTranscriptStart returns the transcript line offset at which this checkpoint's data begins.
// Prefers CheckpointTranscriptStart; falls back to deprecated TranscriptLinesAtStart for old data.
func (m CommittedMetadata) GetTranscriptStart() int {
	if m.CheckpointTranscriptStart > 0 {
		return m.CheckpointTranscriptStart
	}
	return m.TranscriptLinesAtStart
}

