   134→func newDisableCmd() *cobra.Command {
   135→	var useProjectSettings bool
   136→	var uninstall bool
   137→	var force bool
   138→
   139→	cmd := &cobra.Command{
   140→		Use:   "disable",
   141→		Short: "Disable Entire",
   142→		Long: `Disable Entire temporarily. Hooks will exit silently and commands will show a disabled message.
   143→
   144→Use --uninstall to completely remove Entire from this repository, including:
   145→  - .entire/ directory (settings, logs, metadata)
   146→  - Git hooks (prepare-commit-msg, commit-msg, post-commit, pre-push)
   147→  - Session state files (.git/entire-sessions/)
   148→  - Shadow branches (entire/<hash>)
   149→  - Agent hooks (Claude Code, Gemini CLI)`,
   150→		RunE: func(cmd *cobra.Command, _ []string) error {
   151→			if uninstall {
   152→				return runUninstall(cmd.OutOrStdout(), cmd.ErrOrStderr(), force)
   153→			}
   154→			return runDisable(cmd.OutOrStdout(), useProjectSettings)
   155→		},
   156→	}
