   165→// isFullyEnabled checks whether Entire is already fully set up.
   166→// Returns whether it's fully enabled, and if so, the agent type display name and config file path.
   167→func isFullyEnabled() (enabled bool, agentDesc string, configPath string) {
   168→	// Check settings exist and Enabled == true
   169→	s, err := LoadEntireSettings()
   170→	if err != nil || !s.Enabled {
   171→		return false, "", ""
   172→	}
   173→
   174→	// Check any agent hooks installed (not just Claude Code — works with Gemini too)
   175→	installedAgents := GetAgentsWithHooksInstalled()
   176→	if len(installedAgents) == 0 {
   177→		return false, "", ""
   178→	}
   179→
   180→	// Check git hooks installed
   181→	if !strategy.IsGitHookInstalled() {
   182→		return false, "", ""
   183→	}
   184→
   185→	// Check .entire directory exists
   186→	if !checkEntireDirExists() {
   187→		return false, "", ""
   188→	}
   189→
   190→	// Determine agent description from first installed agent
   191→	desc := string(installedAgents[0]) // fallback to agent name
   192→	if ag, err := agent.Get(installedAgents[0]); err == nil {
   193→		desc = string(ag.Type())
   194→	}
   195→
   196→	// Determine config path - check if local settings exists, otherwise show project settings
   197→	entireDirAbs, err := paths.AbsPath(paths.EntireDir)
   198→	if err != nil {
   199→		entireDirAbs = paths.EntireDir
   200→	}
   201→	configDisplay := configDisplayProject
   202→	localSettingsPath := filepath.Join(entireDirAbs, "settings.local.json")
   203→	if _, err := os.Stat(localSettingsPath); err == nil {
   204→		configDisplay = configDisplayLocal
   205→	}
   206→
   207→	return true, desc, configDisplay
   208→}
