   210→// runEnableWithStrategy enables Entire with a specified strategy (non-interactive).
   211→// The selectedStrategy can be either a display name (manual-commit, auto-commit)
   212→// or an internal name (manual-commit, auto-commit).
   213→func runEnableWithStrategy(w io.Writer, selectedStrategy string, localDev, _, useLocalSettings, useProjectSettings, forceHooks, skipPushSessions, telemetry bool) error {
   214→	// Map the strategy to internal name if it's a display name
   215→	internalStrategy := selectedStrategy
   216→	if mapped, ok := strategyDisplayToInternal[selectedStrategy]; ok {
   217→		internalStrategy = mapped
   218→	}
   219→
   220→	// Validate the strategy exists
   221→	strat, err := strategy.Get(internalStrategy)
   222→	if err != nil {
   223→		return fmt.Errorf("unknown strategy: %s (use manual-commit or auto-commit)", selectedStrategy)
   224→	}
   225→
   226→	// Detect default agent
   227→	ag := agent.Default()
   228→	agentType := string(agent.AgentTypeClaudeCode)
   229→	if ag != nil {
   230→		agentType = string(ag.Type())
   231→	}
   232→	fmt.Fprintf(w, "Agent: %s (use --agent to change)\n\n", agentType)
   233→
   234→	// Setup Claude Code hooks (agent hooks don't depend on settings)
   235→	if _, err := setupClaudeCodeHook(localDev, forceHooks); err != nil {
   236→		return fmt.Errorf("failed to setup Claude Code hooks: %w", err)
   237→	}
   238→
   239→	// Setup .entire directory
   240→	if _, err := setupEntireDirectory(); err != nil {
   241→		return fmt.Errorf("failed to setup .entire directory: %w", err)
   242→	}
   243→
   244→	// Load existing settings to preserve other options (like strategy_options.push)
   245→	settings, err := LoadEntireSettings()
   246→	if err != nil {
   247→		// If we can't load, start with defaults
   248→		settings = &EntireSettings{}
   249→	}
   250→	// Update the specific fields
   251→	settings.Strategy = internalStrategy
   252→	settings.LocalDev = localDev
   253→	settings.Enabled = true
   254→
   255→	// Set push_sessions option if --skip-push-sessions flag was provided
   256→	if skipPushSessions {
   257→		if settings.StrategyOptions == nil {
   258→			settings.StrategyOptions = make(map[string]interface{})
   259→		}
   260→		settings.StrategyOptions["push_sessions"] = false
   261→	}
   262→
   263→	// Handle telemetry for non-interactive mode
   264→	// Note: if telemetry is nil (not configured), it defaults to disabled
   265→	if !telemetry || os.Getenv("ENTIRE_TELEMETRY_OPTOUT") != "" {
   266→		f := false
   267→		settings.Telemetry = &f
   268→	}
   269→
   270→	// Determine which settings file to write to
   271→	entireDirAbs, err := paths.AbsPath(paths.EntireDir)
   272→	if err != nil {
   273→		entireDirAbs = paths.EntireDir // Fallback to relative
   274→	}
   275→	shouldUseLocal, showNotification := determineSettingsTarget(entireDirAbs, useLocalSettings, useProjectSettings)
   276→
   277→	if showNotification {
   278→		fmt.Fprintln(w, "Info: Project settings exist. Saving to settings.local.json instead.")
   279→		fmt.Fprintln(w, "  Use --project to update the project settings file.")
   280→	}
   281→
   282→	configDisplay := configDisplayProject
   283→	if shouldUseLocal {
   284→		if err := SaveEntireSettingsLocal(settings); err != nil {
   285→			return fmt.Errorf("failed to save local settings: %w", err)
   286→		}
   287→		configDisplay = configDisplayLocal
   288→	} else {
   289→		if err := SaveEntireSettings(settings); err != nil {
   290→			return fmt.Errorf("failed to save settings: %w", err)
   291→		}
   292→	}
   293→
   294→	// Install git hooks AFTER saving settings (InstallGitHook reads local_dev from settings)
   295→	if _, err := strategy.InstallGitHook(true); err != nil {
   296→		return fmt.Errorf("failed to install git hooks: %w", err)
   297→	}
   298→	fmt.Fprintln(w, "✓ Hooks installed")
   299→	fmt.Fprintf(w, "✓ Project configured (%s)\n", configDisplay)
   300→
   301→	// Let the strategy handle its own setup requirements
   302→	if err := strat.EnsureSetup(); err != nil {
   303→		return fmt.Errorf("failed to setup strategy: %w", err)
   304→	}
   305→
   306→	fmt.Fprintln(w, "\nReady.")
   307→
   308→	return nil
   309→}
