   311→// runEnableInteractive runs the interactive enable flow.
   312→func runEnableInteractive(w io.Writer, localDev, _, useLocalSettings, useProjectSettings, forceHooks, skipPushSessions, telemetry bool) error {
   313→	// Check if already fully enabled — show summary and return early.
   314→	// Skip early return if any configuration flags are set (user wants to reconfigure).
   315→	hasConfigFlags := forceHooks || skipPushSessions || !telemetry || useLocalSettings || useProjectSettings || localDev
   316→	if !hasConfigFlags {
   317→		if fullyEnabled, agentDesc, configPath := isFullyEnabled(); fullyEnabled {
   318→			fmt.Fprintln(w, "Already enabled. Everything looks good.")
   319→			fmt.Fprintln(w)
   320→			fmt.Fprintf(w, "  Agent: %s\n", agentDesc)
   321→			fmt.Fprintf(w, "  Config: %s\n", configPath)
   322→			return nil
   323→		}
   324→	}
   325→
   326→	// Detect default agent
   327→	ag := agent.Default()
   328→	agentType := string(agent.AgentTypeClaudeCode)
   329→	if ag != nil {
   330→		agentType = string(ag.Type())
   331→	}
   332→	fmt.Fprintf(w, "Agent: %s (use --agent to change)\n\n", agentType)
   333→
   334→	// Setup Claude Code hooks (agent hooks don't depend on settings)
   335→	if _, err := setupClaudeCodeHook(localDev, forceHooks); err != nil {
   336→		return fmt.Errorf("failed to setup Claude Code hooks: %w", err)
   337→	}
   338→
   339→	// Setup .entire directory
   340→	if _, err := setupEntireDirectory(); err != nil {
   341→		return fmt.Errorf("failed to setup .entire directory: %w", err)
   342→	}
   343→
   344→	// Use the default strategy (manual-commit)
   345→	internalStrategy := strategy.DefaultStrategyName
   346→
   347→	// Load existing settings to preserve other options (like strategy_options.push)
   348→	settings, err := LoadEntireSettings()
   349→	if err != nil {
   350→		// If we can't load, start with defaults
   351→		settings = &EntireSettings{}
   352→	}
   353→	// Update the specific fields
   354→	settings.Strategy = internalStrategy
   355→	settings.LocalDev = localDev
   356→	settings.Enabled = true
   357→
   358→	// Set push_sessions option if --skip-push-sessions flag was provided
   359→	if skipPushSessions {
   360→		if settings.StrategyOptions == nil {
   361→			settings.StrategyOptions = make(map[string]interface{})
   362→		}
   363→		settings.StrategyOptions["push_sessions"] = false
   364→	}
   365→
   366→	// Determine which settings file to write to
   367→	// First run always creates settings.json (no prompt)
   368→	entireDirAbs, err := paths.AbsPath(paths.EntireDir)
   369→	if err != nil {
   370→		entireDirAbs = paths.EntireDir // Fallback to relative
   371→	}
   372→	shouldUseLocal, showNotification := determineSettingsTarget(entireDirAbs, useLocalSettings, useProjectSettings)
   373→
   374→	if showNotification {
   375→		fmt.Fprintln(w, "Info: Project settings exist. Saving to settings.local.json instead.")
   376→		fmt.Fprintln(w, "  Use --project to update the project settings file.")
   377→	}
   378→
   379→	// Helper to save settings to the appropriate file
   380→	saveSettings := func() error {
   381→		if shouldUseLocal {
   382→			return SaveEntireSettingsLocal(settings)
   383→		}
   384→		return SaveEntireSettings(settings)
   385→	}
   386→
   387→	// Save settings before telemetry prompt so config is persisted even if the user cancels
   388→	if err := saveSettings(); err != nil {
   389→		return fmt.Errorf("failed to save settings: %w", err)
   390→	}
   391→
   392→	// Install git hooks AFTER saving settings (InstallGitHook reads local_dev from settings)
   393→	if _, err := strategy.InstallGitHook(true); err != nil {
   394→		return fmt.Errorf("failed to install git hooks: %w", err)
   395→	}
   396→	fmt.Fprintln(w, "✓ Hooks installed")
   397→
   398→	configDisplay := configDisplayProject
   399→	if shouldUseLocal {
   400→		configDisplay = configDisplayLocal
   401→	}
   402→	fmt.Fprintf(w, "✓ Project configured (%s)\n", configDisplay)
   403→
   404→	// Ask about telemetry consent (only if not already asked)
   405→	fmt.Fprintln(w)
   406→	if err := promptTelemetryConsent(settings, telemetry); err != nil {
   407→		return fmt.Errorf("telemetry consent: %w", err)
   408→	}
   409→	// Save again to persist telemetry choice
   410→	if err := saveSettings(); err != nil {
   411→		return fmt.Errorf("failed to save settings: %w", err)
   412→	}
   413→
   414→	// Let the strategy handle its own setup requirements
   415→	strat, err := strategy.Get(internalStrategy)
   416→	if err != nil {
   417→		return fmt.Errorf("failed to get strategy: %w", err)
   418→	}
   419→	if err := strat.EnsureSetup(); err != nil {
   420→		return fmt.Errorf("failed to setup strategy: %w", err)
   421→	}
   422→
   423→	fmt.Fprintln(w, "\nReady.")
   424→
   425→	return nil
   426→}
