   428→// runEnable is a simple enable that just sets the enabled flag (for programmatic use).
   429→func runEnable(w io.Writer) error {
   430→	settings, err := LoadEntireSettings()
   431→	if err != nil {
   432→		return fmt.Errorf("failed to load settings: %w", err)
   433→	}
   434→
   435→	settings.Enabled = true
   436→	if err := SaveEntireSettings(settings); err != nil {
   437→		return fmt.Errorf("failed to save settings: %w", err)
   438→	}
   439→
   440→	fmt.Fprintln(w, "Entire is now enabled.")
   441→	return nil
   442→}
