   444→func runDisable(w io.Writer, useProjectSettings bool) error {
   445→	settings, err := LoadEntireSettings()
   446→	if err != nil {
   447→		return fmt.Errorf("failed to load settings: %w", err)
   448→	}
   449→
   450→	settings.Enabled = false
   451→
   452→	// If --project flag is specified, always write to project settings
   453→	if useProjectSettings {
   454→		if err := SaveEntireSettings(settings); err != nil {
   455→			return fmt.Errorf("failed to save settings: %w", err)
   456→		}
   457→	} else {
   458→		// Always write to local settings file (create if doesn't exist)
   459→		if err := SaveEntireSettingsLocal(settings); err != nil {
   460→			return fmt.Errorf("failed to save local settings: %w", err)
   461→		}
   462→	}
   463→
   464→	fmt.Fprintln(w, "Entire is now disabled.")
   465→	return nil
   466→}
