   487→// setupClaudeCodeHook sets up Claude Code hooks.
   488→// This is a convenience wrapper that uses the agent package.
   489→// Returns the number of hooks installed (0 if already installed).
   490→func setupClaudeCodeHook(localDev, forceHooks bool) (int, error) {
   491→	ag, err := agent.Get(agent.AgentNameClaudeCode)
   492→	if err != nil {
   493→		return 0, fmt.Errorf("failed to get claude-code agent: %w", err)
   494→	}
   495→
   496→	hookAgent, ok := ag.(agent.HookSupport)
   497→	if !ok {
   498→		return 0, errors.New("claude-code agent does not support hooks")
   499→	}
   500→
   501→	count, err := hookAgent.InstallHooks(localDev, forceHooks)
   502→	if err != nil {
   503→		return 0, fmt.Errorf("failed to install claude-code hooks: %w", err)
   504→	}
   505→
   506→	return count, nil
   507→}
