   509→// printMissingAgentError writes a helpful error listing available agents.
   510→func printMissingAgentError(w io.Writer) {
   511→	agents := agent.List()
   512→	fmt.Fprintln(w, "Missing agent name. Available agents:")
   513→	fmt.Fprintln(w)
   514→	for _, a := range agents {
   515→		suffix := ""
   516→		if a == agent.DefaultAgentName {
   517→			suffix = "    (default)"
   518→		}
   519→		fmt.Fprintf(w, "  %s%s\n", a, suffix)
   520→	}
   521→	fmt.Fprintln(w)
   522→	fmt.Fprintln(w, "Usage: entire enable --agent <agent-name>")
   523→}
