   525→// setupAgentHooksNonInteractive sets up hooks for a specific agent non-interactively.
   526→// If strategyName is provided, it sets the strategy; otherwise uses default.
   527→func setupAgentHooksNonInteractive(w io.Writer, ag agent.Agent, strategyName string, localDev, forceHooks, skipPushSessions, telemetry bool) error {
   528→	agentName := ag.Name()
   529→	// Check if agent supports hooks
   530→	hookAgent, ok := ag.(agent.HookSupport)
   531→	if !ok {
   532→		return fmt.Errorf("agent %s does not support hooks", agentName)
   533→	}
   534→
   535→	fmt.Fprintf(w, "Agent: %s\n\n", ag.Type())
   536→
   537→	// Install agent hooks (agent hooks don't depend on settings)
   538→	if _, err := hookAgent.InstallHooks(localDev, forceHooks); err != nil {
   539→		return fmt.Errorf("failed to install hooks for %s: %w", agentName, err)
   540→	}
   541→
   542→	// Setup .entire directory
   543→	if _, err := setupEntireDirectory(); err != nil {
   544→		return fmt.Errorf("failed to setup .entire directory: %w", err)
   545→	}
   546→
   547→	// Load existing settings to preserve other options (like strategy_options.push)
   548→	settings, err := LoadEntireSettings()
   549→	if err != nil {
   550→		// If we can't load, start with defaults
   551→		settings = &EntireSettings{Strategy: strategy.DefaultStrategyName}
   552→	}
   553→	settings.Enabled = true
   554→	if localDev {
   555→		settings.LocalDev = localDev
   556→	}
   557→
   558→	// Set push_sessions option if --skip-push-sessions flag was provided
   559→	if skipPushSessions {
   560→		if settings.StrategyOptions == nil {
   561→			settings.StrategyOptions = make(map[string]interface{})
   562→		}
   563→		settings.StrategyOptions["push_sessions"] = false
   564→	}
   565→
   566→	// Set strategy if provided
   567→	if strategyName != "" {
   568→		// Map display name to internal name if needed
   569→		internalStrategy := strategyName
   570→		if mapped, ok := strategyDisplayToInternal[strategyName]; ok {
   571→			internalStrategy = mapped
   572→		}
   573→		// Validate the strategy exists
   574→		if _, err := strategy.Get(internalStrategy); err != nil {
   575→			return fmt.Errorf("unknown strategy: %s (use manual-commit or auto-commit)", strategyName)
   576→		}
   577→		settings.Strategy = internalStrategy
   578→	}
   579→
   580→	// Handle telemetry for non-interactive mode
   581→	// Note: if telemetry is nil (not configured), it defaults to disabled
   582→	if !telemetry || os.Getenv("ENTIRE_TELEMETRY_OPTOUT") != "" {
   583→		f := false
   584→		settings.Telemetry = &f
   585→	}
   586→
   587→	if err := SaveEntireSettings(settings); err != nil {
   588→		return fmt.Errorf("failed to save settings: %w", err)
   589→	}
   590→
   591→	// Install git hooks AFTER saving settings (InstallGitHook reads local_dev from settings)
   592→	if _, err := strategy.InstallGitHook(true); err != nil {
   593→		return fmt.Errorf("failed to install git hooks: %w", err)
   594→	}
   595→
   596→	if agentName == agent.AgentNameGemini {
   597→		fmt.Fprintln(w, "✓ Hooks installed - This is a work in progress")
   598→	} else {
   599→		fmt.Fprintln(w, "✓ Hooks installed")
   600→	}
   601→	fmt.Fprintf(w, "✓ Project configured (%s)\n", configDisplayProject)
   602→
   603→	// Let the strategy handle its own setup requirements (creates entire/sessions branch, etc.)
   604→	strat, err := strategy.Get(settings.Strategy)
   605→	if err != nil {
   606→		return fmt.Errorf("failed to get strategy: %w", err)
   607→	}
   608→	if err := strat.EnsureSetup(); err != nil {
   609→		return fmt.Errorf("failed to setup strategy: %w", err)
   610→	}
   611→
   612→	fmt.Fprintln(w, "\nReady.")
   613→
   614→	return nil
   615→}
