   720→// shellCompletionComment is the comment preceding the completion line
   721→const shellCompletionComment = "# Entire CLI shell completion"
   722→
   723→// errUnsupportedShell is returned when the user's shell is not supported for completion.
   724→var errUnsupportedShell = errors.New("unsupported shell")
   725→
   726→// shellCompletionTarget returns the rc file path and completion lines for the
   727→// user's current shell.
   728→func shellCompletionTarget() (shellName, rcFile, completionLine string, err error) {
   729→	home, err := os.UserHomeDir()
   730→	if err != nil {
   731→		return "", "", "", fmt.Errorf("cannot determine home directory: %w", err)
   732→	}
   733→
   734→	shell := os.Getenv("SHELL")
   735→	switch {
   736→	case strings.Contains(shell, "zsh"):
   737→		return "Zsh",
   738→			filepath.Join(home, ".zshrc"),
   739→			"autoload -Uz compinit && compinit && source <(entire completion zsh)",
   740→			nil
   741→	case strings.Contains(shell, "bash"):
   742→		bashRC := filepath.Join(home, ".bashrc")
   743→		if _, err := os.Stat(filepath.Join(home, ".bash_profile")); err == nil {
   744→			bashRC = filepath.Join(home, ".bash_profile")
   745→		}
   746→		return "Bash",
   747→			bashRC,
   748→			"source <(entire completion bash)",
   749→			nil
   750→	case strings.Contains(shell, "fish"):
   751→		return "Fish",
   752→			filepath.Join(home, ".config", "fish", "config.fish"),
   753→			"entire completion fish | source",
   754→			nil
   755→	default:
   756→		return "", "", "", errUnsupportedShell
   757→	}
   758→}
   759→
   760→// promptShellCompletion offers to add shell completion to the user's rc file.
   761→// Only prompts if completion is not already configured.
   762→func promptShellCompletion(w io.Writer) error {
   763→	shellName, rcFile, completionLine, err := shellCompletionTarget()
   764→	if err != nil {
   765→		if errors.Is(err, errUnsupportedShell) {
   766→			fmt.Fprintf(w, "Note: Shell completion not available for your shell. Supported: zsh, bash, fish.\n")
   767→			return nil
   768→		}
   769→		return fmt.Errorf("shell completion: %w", err)
   770→	}
   771→
   772→	if isCompletionConfigured(rcFile) {
   773→		fmt.Fprintf(w, "✓ Shell completion already configured in %s\n", rcFile)
   774→		return nil
   775→	}
   776→
   777→	var selected string
   778→	form := NewAccessibleForm(
   779→		huh.NewGroup(
   780→			huh.NewSelect[string]().
   781→				Title(fmt.Sprintf("Enable shell completion? (detected: %s)", shellName)).
   782→				Options(
   783→					huh.NewOption("Yes", "yes"),
   784→					huh.NewOption("No", "no"),
   785→				).
   786→				Value(&selected),
   787→		),
   788→	)
   789→
   790→	if err := form.Run(); err != nil {
   791→		//nolint:nilerr // User cancelled - not a fatal error, just skip
   792→		return nil
   793→	}
   794→
   795→	if selected != "yes" {
   796→		return nil
   797→	}
   798→
   799→	if err := appendShellCompletion(rcFile, completionLine); err != nil {
   800→		return fmt.Errorf("failed to update %s: %w", rcFile, err)
   801→	}
   802→
   803→	fmt.Fprintf(w, "✓ Shell completion added to %s\n", rcFile)
   804→	fmt.Fprintln(w, "  Restart your shell to activate")
   805→
   806→	return nil
   807→}
   808→
   809→// isCompletionConfigured checks if shell completion is already in the rc file.
   810→func isCompletionConfigured(rcFile string) bool {
   811→	//nolint:gosec // G304: rcFile is constructed from home dir + known filename, not user input
   812→	content, err := os.ReadFile(rcFile)
   813→	if err != nil {
   814→		return false // File doesn't exist or can't read, treat as not configured
   815→	}
   816→	return strings.Contains(string(content), "entire completion")
   817→}
   818→
   819→// appendShellCompletion adds the completion line to the rc file.
   820→func appendShellCompletion(rcFile, completionLine string) error {
   821→	if err := os.MkdirAll(filepath.Dir(rcFile), 0o700); err != nil {
   822→		return fmt.Errorf("creating directory: %w", err)
   823→	}
   824→	//nolint:gosec // G302: Shell rc files need 0644 for user readability
   825→	f, err := os.OpenFile(rcFile, os.O_APPEND|os.O_CREATE|os.O_WRONLY, 0o644)
   826→	if err != nil {
   827→		return fmt.Errorf("opening file: %w", err)
   828→	}
   829→	defer f.Close()
   830→
   831→	_, err = f.WriteString("\n" + shellCompletionComment + "\n" + completionLine + "\n")
   832→	if err != nil {
   833→		return fmt.Errorf("writing completion: %w", err)
   834→	}
   835→	return nil
   836→}
