   881→// runUninstall completely removes Entire from the repository.
   882→func runUninstall(w, errW io.Writer, force bool) error {
   883→	// Check if we're in a git repository
   884→	if _, err := paths.RepoRoot(); err != nil {
   885→		fmt.Fprintln(errW, "Not a git repository. Nothing to uninstall.")
   886→		return NewSilentError(errors.New("not a git repository"))
   887→	}
   888→
   889→	// Gather counts for display
   890→	sessionStateCount := countSessionStates()
   891→	shadowBranchCount := countShadowBranches()
   892→	gitHooksInstalled := strategy.IsGitHookInstalled()
   893→	claudeHooksInstalled := checkClaudeCodeHooksInstalled()
   894→	geminiHooksInstalled := checkGeminiCLIHooksInstalled()
   895→	entireDirExists := checkEntireDirExists()
   896→
   897→	// Check if there's anything to uninstall
   898→	if !entireDirExists && !gitHooksInstalled && sessionStateCount == 0 &&
   899→		shadowBranchCount == 0 && !claudeHooksInstalled && !geminiHooksInstalled {
   900→		fmt.Fprintln(w, "Entire is not installed in this repository.")
   901→		return nil
   902→	}
   903→
   904→	// Show confirmation prompt unless --force
   905→	if !force {
   906→		fmt.Fprintln(w, "\nThis will completely remove Entire from this repository:")
   907→		if entireDirExists {
   908→			fmt.Fprintln(w, "  - .entire/ directory")
   909→		}
   910→		if gitHooksInstalled {
   911→			fmt.Fprintln(w, "  - Git hooks (prepare-commit-msg, commit-msg, post-commit, pre-push)")
   912→		}
   913→		if sessionStateCount > 0 {
   914→			fmt.Fprintf(w, "  - Session state files (%d)\n", sessionStateCount)
   915→		}
   916→		if shadowBranchCount > 0 {
   917→			fmt.Fprintf(w, "  - Shadow branches (%d)\n", shadowBranchCount)
   918→		}
   919→		switch {
   920→		case claudeHooksInstalled && geminiHooksInstalled:
   921→			fmt.Fprintln(w, "  - Agent hooks (Claude Code, Gemini CLI)")
   922→		case claudeHooksInstalled:
   923→			fmt.Fprintln(w, "  - Agent hooks (Claude Code)")
   924→		case geminiHooksInstalled:
   925→			fmt.Fprintln(w, "  - Agent hooks (Gemini CLI)")
   926→		}
   927→		fmt.Fprintln(w)
   928→
   929→		var confirmed bool
   930→		form := NewAccessibleForm(
   931→			huh.NewGroup(
   932→				huh.NewConfirm().
   933→					Title("Are you sure you want to uninstall Entire?").
   934→					Affirmative("Yes, uninstall").
   935→					Negative("Cancel").
   936→					Value(&confirmed),
   937→			),
   938→		)
   939→
   940→		if err := form.Run(); err != nil {
   941→			return fmt.Errorf("confirmation cancelled: %w", err)
   942→		}
   943→
   944→		if !confirmed {
   945→			fmt.Fprintln(w, "Uninstall cancelled.")
   946→			return nil
   947→		}
   948→	}
   949→
   950→	fmt.Fprintln(w, "\nUninstalling Entire CLI...")
   951→
   952→	// 1. Remove agent hooks (lowest risk)
   953→	if err := removeAgentHooks(w); err != nil {
   954→		fmt.Fprintf(errW, "Warning: failed to remove agent hooks: %v\n", err)
   955→	}
   956→
   957→	// 2. Remove git hooks
   958→	removed, err := strategy.RemoveGitHook()
   959→	if err != nil {
   960→		fmt.Fprintf(errW, "Warning: failed to remove git hooks: %v\n", err)
   961→	} else if removed > 0 {
   962→		fmt.Fprintf(w, "  Removed git hooks (%d)\n", removed)
   963→	}
   964→
   965→	// 3. Remove session state files
   966→	statesRemoved, err := removeAllSessionStates()
   967→	if err != nil {
   968→		fmt.Fprintf(errW, "Warning: failed to remove session states: %v\n", err)
   969→	} else if statesRemoved > 0 {
   970→		fmt.Fprintf(w, "  Removed session states (%d)\n", statesRemoved)
   971→	}
   972→
   973→	// 4. Remove .entire/ directory
   974→	if err := removeEntireDirectory(); err != nil {
   975→		fmt.Fprintf(errW, "Warning: failed to remove .entire directory: %v\n", err)
   976→	} else if entireDirExists {
   977→		fmt.Fprintln(w, "  Removed .entire directory")
   978→	}
   979→
   980→	// 5. Remove shadow branches
   981→	branchesRemoved, err := removeAllShadowBranches()
   982→	if err != nil {
   983→		fmt.Fprintf(errW, "Warning: failed to remove shadow branches: %v\n", err)
   984→	} else if branchesRemoved > 0 {
   985→		fmt.Fprintf(w, "  Removed %d shadow branches\n", branchesRemoved)
   986→	}
   987→
   988→	fmt.Fprintln(w, "\nEntire CLI uninstalled successfully.")
   989→	return nil
   990→}
