  1050→// removeAgentHooks removes hooks from all agents that support hooks.
  1051→func removeAgentHooks(w io.Writer) error {
  1052→	var errs []error
  1053→
  1054→	// Remove Claude Code hooks
  1055→	claudeAgent, err := agent.Get(agent.AgentNameClaudeCode)
  1056→	if err == nil {
  1057→		if hookAgent, ok := claudeAgent.(agent.HookSupport); ok {
  1058→			wasInstalled := hookAgent.AreHooksInstalled()
  1059→			if err := hookAgent.UninstallHooks(); err != nil {
  1060→				errs = append(errs, err)
  1061→			} else if wasInstalled {
  1062→				fmt.Fprintln(w, "  Removed Claude Code hooks")
  1063→			}
  1064→		}
  1065→	}
  1066→
  1067→	// Remove Gemini CLI hooks
  1068→	geminiAgent, err := agent.Get(agent.AgentNameGemini)
  1069→	if err == nil {
  1070→		if hookAgent, ok := geminiAgent.(agent.HookSupport); ok {
  1071→			wasInstalled := hookAgent.AreHooksInstalled()
  1072→			if err := hookAgent.UninstallHooks(); err != nil {
  1073→				errs = append(errs, err)
  1074→			} else if wasInstalled {
  1075→				fmt.Fprintln(w, "  Removed Gemini CLI hooks")
  1076→			}
  1077→		}
  1078→	}
  1079→
  1080→	return errors.Join(errs...)
  1081→}
