  1087→		return 0, fmt.Errorf("failed to create state store: %w", err)
  1088→	}
  1089→
  1090→	// Count states before removing
  1091→	states, err := store.List(context.Background())
  1092→	if err != nil {
  1093→		return 0, fmt.Errorf("failed to list session states: %w", err)
  1094→	}
  1095→	count := len(states)
