  1097→	// Remove the entire directory
  1098→	if err := store.RemoveAll(); err != nil {
  1099→		return 0, fmt.Errorf("failed to remove session states: %w", err)
  1100→	}
  1101→
  1102→	return count, nil
  1103→}
