     1→package cli
     2→
     3→import (
     4→	"context"
     5→	"errors"
     6→	"fmt"
     7→	"io"
     8→	"io/fs"
     9→	"os"
    10→	"os/exec"
    11→	"sort"
    12→	"strings"
    13→	"time"
    14→
    15→	"github.com/entireio/cli/cmd/entire/cli/paths"
    16→	"github.com/entireio/cli/cmd/entire/cli/session"
    17→	"github.com/entireio/cli/cmd/entire/cli/settings"
    18→	"github.com/entireio/cli/cmd/entire/cli/stringutil"
    19→
    20→	"github.com/spf13/cobra"
    21→)
    22→
    23→func newStatusCmd() *cobra.Command {
    24→	var detailed bool
    25→
    26→	cmd := &cobra.Command{
    27→		Use:   "status",
    28→		Short: "Show Entire status",
    29→		Long:  "Show whether Entire is currently enabled or disabled",
    30→		RunE: func(cmd *cobra.Command, _ []string) error {
    31→			return runStatus(cmd.OutOrStdout(), detailed)
    32→		},
    33→	}
    34→
    35→	cmd.Flags().BoolVar(&detailed, "detailed", false, "Show detailed status for each settings file")
    36→
    37→	return cmd
    38→}
    39→
    40→func runStatus(w io.Writer, detailed bool) error {
    41→	// Check if we're in a git repository
    42→	if _, repoErr := paths.RepoRoot(); repoErr != nil {
    43→		fmt.Fprintln(w, "✕ not a git repository")
    44→		return nil //nolint:nilerr // Not being in a git repo is a valid status, not an error
    45→	}
    46→
    47→	// Get absolute paths for settings files
    48→	settingsPath, err := paths.AbsPath(EntireSettingsFile)
    49→	if err != nil {
    50→		settingsPath = EntireSettingsFile
    51→	}
    52→	localSettingsPath, err := paths.AbsPath(EntireSettingsLocalFile)
    53→	if err != nil {
    54→		localSettingsPath = EntireSettingsLocalFile
    55→	}
    56→
    57→	// Check which settings files exist
    58→	_, projectErr := os.Stat(settingsPath)
    59→	if projectErr != nil && !errors.Is(projectErr, fs.ErrNotExist) {
    60→		return fmt.Errorf("cannot access project settings file: %w", projectErr)
    61→	}
    62→	_, localErr := os.Stat(localSettingsPath)
    63→	if localErr != nil && !errors.Is(localErr, fs.ErrNotExist) {
    64→		return fmt.Errorf("cannot access local settings file: %w", localErr)
    65→	}
    66→	projectExists := projectErr == nil
    67→	localExists := localErr == nil
    68→
    69→	if !projectExists && !localExists {
    70→		fmt.Fprintln(w, "○ not set up (run `entire enable` to get started)")
    71→		return nil
    72→	}
    73→
    74→	if detailed {
    75→		return runStatusDetailed(w, settingsPath, localSettingsPath, projectExists, localExists)
    76→	}
    77→
    78→	// Short output: just show the effective/merged state
    79→	settings, err := LoadEntireSettings()
    80→	if err != nil {
    81→		return fmt.Errorf("failed to load settings: %w", err)
    82→	}
    83→
    84→	fmt.Fprintln(w, formatSettingsStatusShort(settings))
    85→
    86→	if settings.Enabled {
    87→		writeActiveSessions(w)
    88→	}
    89→
    90→	return nil
    91→}
    92→
    93→// runStatusDetailed shows the effective status plus detailed status for each settings file.
    94→func runStatusDetailed(w io.Writer, settingsPath, localSettingsPath string, projectExists, localExists bool) error {
    95→	// First show the effective/merged status
    96→	effectiveSettings, err := LoadEntireSettings()
    97→	if err != nil {
    98→		return fmt.Errorf("failed to load settings: %w", err)
    99→	}
   100→	fmt.Fprintln(w, formatSettingsStatusShort(effectiveSettings))
   101→	fmt.Fprintln(w) // blank line
   102→
   103→	// Show project settings if it exists
   104→	if projectExists {
   105→		projectSettings, err := settings.LoadFromFile(settingsPath)
   106→		if err != nil {
   107→			return fmt.Errorf("failed to load project settings: %w", err)
   108→		}
   109→		fmt.Fprintln(w, formatSettingsStatus("Project", projectSettings))
   110→	}
   111→
   112→	// Show local settings if it exists
   113→	if localExists {
   114→		localSettings, err := settings.LoadFromFile(localSettingsPath)
   115→		if err != nil {
   116→			return fmt.Errorf("failed to load local settings: %w", err)
   117→		}
   118→		fmt.Fprintln(w, formatSettingsStatus("Local", localSettings))
   119→	}
   120→
   121→	if effectiveSettings.Enabled {
   122→		writeActiveSessions(w)
   123→	}
   124→
   125→	return nil
   126→}
   127→
   128→// formatSettingsStatusShort formats a short settings status line.
   129→// Output format: "Enabled (manual-commit)" or "Disabled (auto-commit)"
   130→func formatSettingsStatusShort(settings *EntireSettings) string {
   131→	displayName := settings.Strategy
   132→	if dn, ok := strategyInternalToDisplay[settings.Strategy]; ok {
   133→		displayName = dn
   134→	}
   135→
   136→	if settings.Enabled {
   137→		return fmt.Sprintf("Enabled (%s)", displayName)
   138→	}
   139→	return fmt.Sprintf("Disabled (%s)", displayName)
   140→}
   141→
   142→// formatSettingsStatus formats a settings status line with source prefix.
   143→// Output format: "Project, enabled (manual-commit)" or "Local, disabled (auto-commit)"
   144→func formatSettingsStatus(prefix string, settings *EntireSettings) string {
   145→	displayName := settings.Strategy
   146→	if dn, ok := strategyInternalToDisplay[settings.Strategy]; ok {
   147→		displayName = dn
   148→	}
   149→
   150→	if settings.Enabled {
   151→		return fmt.Sprintf("%s, enabled (%s)", prefix, displayName)
   152→	}
   153→	return fmt.Sprintf("%s, disabled (%s)", prefix, displayName)
   154→}
   155→
   156→// timeAgo formats a time as a human-readable relative duration.
   157→func timeAgo(t time.Time) string {
   158→	d := time.Since(t)
   159→	switch {
   160→	case d < time.Minute:
   161→		return "just now"
   162→	case d < time.Hour:
   163→		m := int(d.Minutes())
   164→		return fmt.Sprintf("%dm ago", m)
   165→	case d < 24*time.Hour:
   166→		h := int(d.Hours())
   167→		return fmt.Sprintf("%dh ago", h)
   168→	default:
   169→		days := int(d.Hours() / 24)
   170→		return fmt.Sprintf("%dd ago", days)
   171→	}
   172→}
   173→
   174→// worktreeGroup groups sessions by worktree path for display.
   175→type worktreeGroup struct {
   176→	path     string
   177→	branch   string
   178→	sessions []*session.State
   179→}
   180→
   181→const unknownPlaceholder = "(unknown)"
   182→
   183→// writeActiveSessions writes active session information grouped by worktree.
   184→func writeActiveSessions(w io.Writer) {
   185→	store, err := session.NewStateStore()
   186→	if err != nil {
   187→		return
   188→	}
   189→
   190→	states, err := store.List(context.Background())
   191→	if err != nil || len(states) == 0 {
   192→		return
   193→	}
   194→
   195→	// Filter to active sessions only
   196→	var active []*session.State
   197→	for _, s := range states {
   198→		if s.EndedAt == nil {
   199→			active = append(active, s)
   200→		}
   201→	}
   202→	if len(active) == 0 {
   203→		return
   204→	}
   205→
   206→	// Group by worktree path
   207→	groups := make(map[string]*worktreeGroup)
   208→	for _, s := range active {
   209→		wp := s.WorktreePath
   210→		if wp == "" {
   211→			wp = unknownPlaceholder
   212→		}
   213→		g, ok := groups[wp]
   214→		if !ok {
   215→			g = &worktreeGroup{path: wp}
   216→			groups[wp] = g
   217→		}
   218→		g.sessions = append(g.sessions, s)
   219→	}
   220→
   221→	// Resolve branch names for each worktree (skip for unknown paths)
   222→	for _, g := range groups {
   223→		if g.path != unknownPlaceholder {
   224→			g.branch = resolveWorktreeBranch(g.path)
   225→		}
   226→	}
   227→
   228→	// Sort groups: alphabetical by path
   229→	sortedGroups := make([]*worktreeGroup, 0, len(groups))
   230→	for _, g := range groups {
   231→		sortedGroups = append(sortedGroups, g)
   232→	}
   233→	sort.Slice(sortedGroups, func(i, j int) bool {
   234→		return sortedGroups[i].path < sortedGroups[j].path
   235→	})
   236→
   237→	// Sort sessions within each group by StartedAt (newest first)
   238→	for _, g := range sortedGroups {
   239→		sort.Slice(g.sessions, func(i, j int) bool {
   240→			return g.sessions[i].StartedAt.After(g.sessions[j].StartedAt)
   241→		})
   242→	}
   243→
   244→	fmt.Fprintln(w)
   245→	fmt.Fprintln(w, "Active Sessions:")
   246→	for i, g := range sortedGroups {
   247→		header := g.path
   248→		if g.branch != "" {
   249→			header += " (" + g.branch + ")"
   250→		}
   251→		fmt.Fprintf(w, "  %s\n", header)
   252→
   253→		for _, st := range g.sessions {
   254→			agentLabel := string(st.AgentType)
