package strategy

import (
	"context"
	"fmt"
	"time"

	"github.com/entireio/cli/cmd/entire/cli/agent"
	"github.com/entireio/cli/cmd/entire/cli/checkpoint"
	"github.com/entireio/cli/cmd/entire/cli/paths"

	"github.com/go-git/go-git/v5"
	"github.com/go-git/go-git/v5/plumbing"
)

// Shadow strategy session state methods.
// Uses session.StateStore for persistence.

// loadSessionState loads session state using the StateStore.
func (s *ManualCommitStrategy) loadSessionState(sessionID string) (*SessionState, error) {
	store, err := s.getStateStore()
	if err != nil {
		return nil, err
	}
	state, err := store.Load(context.Background(), sessionID)
	if err != nil {
		return nil, fmt.Errorf("failed to load session state: %w", err)
	}
	return sessionStateToStrategy(state), nil
}

// saveSessionState saves session state using the StateStore.
func (s *ManualCommitStrategy) saveSessionState(state *SessionState) error {
	store, err := s.getStateStore()
	if err != nil {
		return err
	}
	if err := store.Save(context.Background(), sessionStateFromStrategy(state)); err != nil {
		return fmt.Errorf("failed to save session state: %w", err)
	}
	return nil
}

// clearSessionState clears session state using the StateStore.
func (s *ManualCommitStrategy) clearSessionState(sessionID string) error {
	store, err := s.getStateStore()
	if err != nil {
		return err
	}
	if err := store.Clear(context.Background(), sessionID); err != nil {
		return fmt.Errorf("failed to clear session state: %w", err)
	}
	return nil
}

// listAllSessionStates returns all active session states.
// It filters out orphaned sessions whose shadow branch no longer exists.
func (s *ManualCommitStrategy) listAllSessionStates() ([]*SessionState, error) {
	store, err := s.getStateStore()
	if err != nil {
		return nil, fmt.Errorf("failed to get state store: %w", err)
	}

	sessionStates, err := store.List(context.Background())
	if err != nil {
		return nil, fmt.Errorf("failed to list session states: %w", err)
	}

	if len(sessionStates) == 0 {
		return nil, nil
	}

	repo, err := OpenRepository()
	if err != nil {
		return nil, fmt.Errorf("failed to open git repository: %w", err)
	}

	var states []*SessionState
	for _, sessionState := range sessionStates {
		state := sessionStateToStrategy(sessionState)

		// Skip and cleanup orphaned sessions whose shadow branch no longer exists
		// Only cleanup if the session has created checkpoints (CheckpointCount > 0)
		// AND has no LastCheckpointID (not recently condensed)
		// Sessions with LastCheckpointID are valid - they were condensed and the shadow
		// branch was intentionally deleted. Keep them for LastCheckpointID reuse.
		shadowBranch := getShadowBranchNameForCommit(state.BaseCommit, state.WorktreeID)
		refName := plumbing.NewBranchReferenceName(shadowBranch)
		if _, err := repo.Reference(refName, true); err != nil {
			// Shadow branch doesn't exist
			// Only cleanup if session has checkpoints AND no LastCheckpointID
			// Sessions with LastCheckpointID should be kept for checkpoint reuse
			if state.CheckpointCount > 0 && state.LastCheckpointID == "" {
				// Clear the orphaned session state (best-effort, don't fail listing)
				//nolint:errcheck,gosec // G104: Cleanup is best-effort, shouldn't fail the list operation
				store.Clear(context.Background(), state.SessionID)
				continue
			}
			// Keep sessions with LastCheckpointID or no checkpoints yet
		}

		states = append(states, state)
	}
	return states, nil
}

// findSessionsForWorktree finds all sessions for the given worktree path.
func (s *ManualCommitStrategy) findSessionsForWorktree(worktreePath string) ([]*SessionState, error) {
	allStates, err := s.listAllSessionStates()
	if err != nil {
		return nil, err
	}

	var matching []*SessionState
	for _, state := range allStates {
		if state.WorktreePath == worktreePath {
			matching = append(matching, state)
		}
	}
	return matching, nil
}

// findSessionsForCommit finds all sessions where base_commit matches the given SHA.
func (s *ManualCommitStrategy) findSessionsForCommit(baseCommitSHA string) ([]*SessionState, error) {
	allStates, err := s.listAllSessionStates()
	if err != nil {
		return nil, err
	}

	var matching []*SessionState
	for _, state := range allStates {
		if state.BaseCommit == baseCommitSHA {
			matching = append(matching, state)
		}
	}
	return matching, nil
}

// FindSessionsForCommit is the exported version of findSessionsForCommit.
// Used by the rewind reset command to find sessions to clean up.
func (s *ManualCommitStrategy) FindSessionsForCommit(baseCommitSHA string) ([]*SessionState, error) {
	return s.findSessionsForCommit(baseCommitSHA)
}

// ClearSessionState is the exported version of clearSessionState.
// Used by the rewind reset command to clean up session state files.
func (s *ManualCommitStrategy) ClearSessionState(sessionID string) error {
	return s.clearSessionState(sessionID)
}

// CountOtherActiveSessionsWithCheckpoints counts how many other active sessions
// from the SAME worktree (different from currentSessionID) have created checkpoints
// on the SAME base commit (current HEAD). This is used to show an informational message
// about concurrent sessions that will be included in the next commit.
// Returns 0, nil if no such sessions exist.
func (s *ManualCommitStrategy) CountOtherActiveSessionsWithCheckpoints(currentSessionID string) (int, error) {
	currentWorktree, err := GetWorktreePath()
	if err != nil {
		return 0, err
	}

	// Get current HEAD to compare with session base commits
	repo, err := OpenRepository()
	if err != nil {
		return 0, err
	}
	head, err := repo.Head()
	if err != nil {
		return 0, fmt.Errorf("failed to get HEAD: %w", err)
	}
	currentHead := head.Hash().String()

	allStates, err := s.listAllSessionStates()
	if err != nil {
		return 0, err
	}

	count := 0
	for _, state := range allStates {
		// Only consider sessions from the same worktree with checkpoints
		// AND based on the same commit (current HEAD)
		// Sessions from different base commits are independent and shouldn't be counted
		if state.SessionID != currentSessionID &&
			state.WorktreePath == currentWorktree &&
			state.CheckpointCount > 0 &&
			state.BaseCommit == currentHead {
			count++
		}
	}
	return count, nil
}

// initializeSession creates a new session state or updates a partial one.
// A partial state may exist if the concurrent session warning was shown.
// agentType is the human-readable name of the agent (e.g., "Claude Code").
// transcriptPath is the path to the live transcript file (for mid-session commit detection).
// userPrompt is the user's prompt text (stored truncated as FirstPrompt for display).
func (s *ManualCommitStrategy) initializeSession(repo *git.Repository, sessionID string, agentType agent.AgentType, transcriptPath string, userPrompt string) (*SessionState, error) {
	head, err := repo.Head()
	if err != nil {
		return nil, fmt.Errorf("failed to get HEAD: %w", err)
	}

	worktreePath, err := GetWorktreePath()
	if err != nil {
		return nil, fmt.Errorf("failed to get worktree path: %w", err)
	}

	// Get worktree ID for shadow branch naming
	worktreeID, err := paths.GetWorktreeID(worktreePath)
	if err != nil {
		return nil, fmt.Errorf("failed to get worktree ID: %w", err)
	}

	// Capture untracked files at session start to preserve them during rewind
	untrackedFiles, err := collectUntrackedFiles()
	if err != nil {
		// Non-fatal: continue even if we can't collect untracked files
		untrackedFiles = nil
	}

	now := time.Now()
	state := &SessionState{
		SessionID:             sessionID,
		BaseCommit:            head.Hash().String(),
		WorktreePath:          worktreePath,
		WorktreeID:            worktreeID,
		StartedAt:             now,
		LastInteractionAt:     &now,
		CheckpointCount:       0,
		UntrackedFilesAtStart: untrackedFiles,
		AgentType:             agentType,
		TranscriptPath:        transcriptPath,
		FirstPrompt:           truncatePromptForStorage(userPrompt),
	}

	if err := s.saveSessionState(state); err != nil {
		return nil, err
	}

	return state, nil
}

// getShadowBranchNameForCommit returns the shadow branch name for the given base commit and worktree ID.
// worktreeID should be empty for the main worktree or the internal git worktree name for linked worktrees.
func getShadowBranchNameForCommit(baseCommit, worktreeID string) string {
	return checkpoint.ShadowBranchNameForCommit(baseCommit, worktreeID)
}

package strategy

import (
	"time"

	"github.com/entireio/cli/cmd/entire/cli/agent"
	"github.com/entireio/cli/cmd/entire/cli/checkpoint/id"
	"github.com/entireio/cli/cmd/entire/cli/stringutil"
)

const (
	// sessionStateDirName is the directory name for session state files within git common dir.
	sessionStateDirName = "entire-sessions"

	// logsOnlyScanLimit is the maximum number of commits to scan for logs-only points.
	logsOnlyScanLimit = 50

	// maxFirstPromptRunes is the maximum rune length for FirstPrompt stored in session state.
	maxFirstPromptRunes = 100
)

// truncatePromptForStorage collapses whitespace and truncates a user prompt
// for storage in FirstPrompt fields.
func truncatePromptForStorage(prompt string) string {
	return stringutil.TruncateRunes(stringutil.CollapseWhitespace(prompt), maxFirstPromptRunes, "...")
}

// SessionState represents the state of an active session.
type SessionState struct {
	SessionID                string          `json:"session_id"`
	BaseCommit               string          `json:"base_commit"`
	WorktreePath             string          `json:"worktree_path,omitempty"` // Absolute path to the worktree root
	WorktreeID               string          `json:"worktree_id,omitempty"`   // Internal git worktree identifier (empty for main worktree)
	StartedAt                time.Time       `json:"started_at"`
	EndedAt                  *time.Time      `json:"ended_at,omitempty"`            // When the session was explicitly closed (nil = active or unclean exit)
	LastInteractionAt        *time.Time      `json:"last_interaction_at,omitempty"` // Last user prompt submit time
	CheckpointCount          int             `json:"checkpoint_count"`
	CondensedTranscriptLines int             `json:"condensed_transcript_lines,omitempty"` // Lines already included in previous condensation
	UntrackedFilesAtStart    []string        `json:"untracked_files_at_start,omitempty"`   // Files that existed at session start (to preserve during rewind)
	FilesTouched             []string        `json:"files_touched,omitempty"`              // Files modified/created/deleted during this session
	LastCheckpointID         id.CheckpointID `json:"last_checkpoint_id,omitempty"`         // Checkpoint ID from last condensation, reused for subsequent commits without new content
	AgentType                agent.AgentType `json:"agent_type,omitempty"`                 // Agent type identifier (e.g., "Claude Code", "Cursor")
	TranscriptPath           string          `json:"transcript_path,omitempty"`            // Path to the live transcript file (for mid-session commit detection)
	FirstPrompt              string          `json:"first_prompt,omitempty"`               // First user prompt that started this session (truncated for display)

	// Token usage tracking (accumulated across all checkpoints in this session)
	TokenUsage *agent.TokenUsage `json:"token_usage,omitempty"`

	// Transcript position when session started (for multi-session checkpoints on entire/sessions)
	TranscriptLinesAtStart      int    `json:"transcript_lines_at_start,omitempty"`
	TranscriptIdentifierAtStart string `json:"transcript_identifier_at_start,omitempty"`

	// PromptAttributions tracks user and agent line changes at each prompt start.
	PromptAttributions []PromptAttribution `json:"prompt_attributions,omitempty"`

	// PendingPromptAttribution holds attribution calculated at prompt start (before agent runs).
	// This is moved to PromptAttributions when SaveChanges is called.
	PendingPromptAttribution *PromptAttribution `json:"pending_prompt_attribution,omitempty"`
}

// PromptAttribution captures line-level attribution data at the start of each prompt,
// calculated BEFORE the agent runs. This allows us to separate user edits (made between
// prompts) from agent edits (made during prompt execution).
//
// Fields:
//   - CheckpointNumber: Which checkpoint this attribution is for (1-indexed)
//   - UserLinesAdded/Removed: User edits since the last checkpoint (lastCheckpoint → worktree)
//   - AgentLinesAdded/Removed: Cumulative agent work so far (base → lastCheckpoint)
//   - UserAddedPerFile: Per-file breakdown of user additions (for accurate modification tracking)
//
// Note: For checkpoint 1, AgentLinesAdded/Removed are always 0 because there is no previous
// checkpoint to compare against. This doesn't mean the agent hasn't done work yet - it means
// we can't measure cumulative agent work until after the first checkpoint is created.
// These fields become meaningful starting from checkpoint 2.
//
// UserAddedPerFile enables accurate tracking of user self-modifications. When a user modifies
// their own previously-added lines (not agent lines), we shouldn't subtract from the agent's
// contribution. See docs/architecture/attribution.md for details.
type PromptAttribution struct {
	CheckpointNumber  int            `json:"checkpoint_number"`
	UserLinesAdded    int            `json:"user_lines_added"`
	UserLinesRemoved  int            `json:"user_lines_removed"`
	AgentLinesAdded   int            `json:"agent_lines_added"`             // Always 0 for checkpoint 1 (no previous checkpoint)
	AgentLinesRemoved int            `json:"agent_lines_removed"`           // Always 0 for checkpoint 1 (no previous checkpoint)
	UserAddedPerFile  map[string]int `json:"user_added_per_file,omitempty"` // Per-file user additions for modification tracking
}

// CheckpointInfo represents checkpoint metadata stored on the sessions branch.
// Metadata is stored at sharded path: <checkpoint_id[:2]>/<checkpoint_id[2:]>/
type CheckpointInfo struct {
	CheckpointID     id.CheckpointID `json:"checkpoint_id"` // 12-hex-char from Entire-Checkpoint trailer, used as directory path
	SessionID        string          `json:"session_id"`
	CreatedAt        time.Time       `json:"created_at"`
	CheckpointsCount int             `json:"checkpoints_count"`
	FilesTouched     []string        `json:"files_touched"`
	Agent            agent.AgentType `json:"agent,omitempty"` // Human-readable agent name (e.g., "Claude Code")
	IsTask           bool            `json:"is_task,omitempty"`
	ToolUseID        string          `json:"tool_use_id,omitempty"`
	SessionCount     int             `json:"session_count,omitempty"` // Number of sessions (1 if omitted)
	SessionIDs       []string        `json:"session_ids,omitempty"`   // All session IDs in this checkpoint
}

// CondenseResult contains the result of a session condensation operation.
type CondenseResult struct {
	CheckpointID         id.CheckpointID // 12-hex-char from Entire-Checkpoint trailer, used as directory path
	SessionID            string
	CheckpointsCount     int
	FilesTouched         []string
	TotalTranscriptLines int // Total lines in transcript after this condensation
}

// ExtractedSessionData contains data extracted from a shadow branch.
type ExtractedSessionData struct {
	Transcript          []byte   // Full transcript content for the session
	FullTranscriptLines int      // Total line count in full transcript
	Prompts             []string // All user prompts from this portion
	Context             []byte   // Generated context.md content
	FilesTouched        []string
	TokenUsage          *agent.TokenUsage // Token usage calculated from transcript (since TranscriptLinesAtStart)
}

