	}

	var buf bytes.Buffer
	writeActiveSessions(&buf)

	output := buf.String()
	if strings.Contains(output, "active") {
		t.Errorf("Expected no 'active' when LastInteractionAt is close to StartedAt, got: %s", output)
	}
}

