	}
	if loadedOld == nil {
		t.Fatal("LoadSessionState() returned nil")
	}

	// Verify LastInteractionAt remains nil
	if loadedOld.LastInteractionAt != nil {
		t.Errorf("LastInteractionAt = %v, want nil for old session", *loadedOld.LastInteractionAt)
	}
}

