	}
	if loaded.StepCount != expected.StepCount {
		t.Errorf("StepCount = %d, want %d", loaded.StepCount, expected.StepCount)
	}
	if loaded.CheckpointTranscriptStart != expected.CheckpointTranscriptStart {
