    20// Strategy display names for user-friendly selection
    21const (
    22	strategyDisplayManualCommit = "manual-commit"
    23	strategyDisplayAutoCommit   = "auto-commit"
    24)
    25
    26// strategyDisplayToInternal maps user-friendly names to internal strategy names
    27var strategyDisplayToInternal = map[string]string{
    28	strategyDisplayManualCommit: strategy.StrategyNameManualCommit,
    29	strategyDisplayAutoCommit:   strategy.StrategyNameAutoCommit,
    30}
    31
