    38func newEnableCmd() *cobra.Command {
    39	var localDev bool
    40	var ignoreUntracked bool
    41	var useLocalSettings bool
    42	var useProjectSettings bool
    43	var agentName string
    44	var strategyFlag string
    45	var forceHooks bool
    46	var setupShell bool
    47	var skipPushSessions bool
    48	var disableMultisessionWarning bool
    49	var telemetry bool
    50
    51	cmd := &cobra.Command{
    52		Use:   "enable",
    53		Short: "Enable Entire",
    54		Long:  "Enable Entire with interactive setup for session tracking mode",
    55		RunE: func(cmd *cobra.Command, _ []string) error {
    56			// Check if we're in a git repository first - this is a prerequisite error,
    57			// not a usage error, so we silence Cobra's output and use SilentError
    58			// to prevent duplicate error output in main.go
    59			if _, err := paths.RepoRoot(); err != nil {
    60				cmd.SilenceUsage = true
    61				cmd.SilenceErrors = true
    62				fmt.Fprintln(cmd.ErrOrStderr(), "Not a git repository. Please run 'entire enable' from within a git repository.")
    63				return NewSilentError(errors.New("not a git repository"))
    64			}
    65
    66			if err := validateSetupFlags(useLocalSettings, useProjectSettings); err != nil {
    67				return err
    68			}
    69			// Non-interactive mode if --agent flag is provided
    70			if agentName != "" {
    71				return setupAgentHooksNonInteractive(agent.AgentName(agentName), strategyFlag, localDev, forceHooks, skipPushSessions, telemetry, disableMultisessionWarning)
    72			}
    73			// If strategy is specified via flag, skip interactive selection
    74			if strategyFlag != "" {
    75				return runEnableWithStrategy(cmd.OutOrStdout(), strategyFlag, localDev, ignoreUntracked, useLocalSettings, useProjectSettings, forceHooks, setupShell, skipPushSessions, telemetry, disableMultisessionWarning)
    76			}
    77			return runEnableInteractive(cmd.OutOrStdout(), localDev, ignoreUntracked, useLocalSettings, useProjectSettings, forceHooks, setupShell, skipPushSessions, telemetry, disableMultisessionWarning)
    78		},
    79	}
    80
    81	cmd.Flags().BoolVar(&localDev, "local-dev", false, "Use go run instead of entire binary for hooks")
    82	cmd.Flags().MarkHidden("local-dev") //nolint:errcheck,gosec // flag is defined above
    83	cmd.Flags().BoolVar(&ignoreUntracked, "ignore-untracked", false, "Commit all new files without tracking pre-existing untracked files")
    84	cmd.Flags().MarkHidden("ignore-untracked") //nolint:errcheck,gosec // flag is defined above
    85	cmd.Flags().BoolVar(&useLocalSettings, "local", false, "Write settings to settings.local.json instead of settings.json")
    86	cmd.Flags().BoolVar(&useProjectSettings, "project", false, "Write settings to settings.json even if it already exists")
    87	cmd.Flags().StringVar(&agentName, "agent", "", "Agent to setup hooks for (e.g., claude-code). Enables non-interactive mode.")
    88	cmd.Flags().StringVar(&strategyFlag, "strategy", "", "Strategy to use (manual-commit or auto-commit)")
    89	cmd.Flags().BoolVarP(&forceHooks, "force", "f", false, "Force reinstall hooks (removes existing Entire hooks first)")
    90	cmd.Flags().BoolVar(&setupShell, "setup-shell", false, "Add shell completion to your rc file (non-interactive)")
    91	cmd.Flags().BoolVar(&skipPushSessions, "skip-push-sessions", false, "Disable automatic pushing of session logs on git push")
    92	cmd.Flags().BoolVar(&disableMultisessionWarning, "disable-multisession-warning", false, "Disable warnings when multiple sessions are active on the same commit")
    93	cmd.Flags().BoolVar(&telemetry, "telemetry", true, "Enable anonymous usage analytics")
    94	//nolint:errcheck,gosec // completion is optional, flag is defined above
    95	cmd.RegisterFlagCompletionFunc("strategy", func(_ *cobra.Command, _ []string, _ string) ([]string, cobra.ShellCompDirective) {
    96		return []string{strategyDisplayManualCommit, strategyDisplayAutoCommit}, cobra.ShellCompDirectiveNoFileComp
    97	})
    98
    99	// Add subcommands for automation/testing
   100	cmd.AddCommand(newSetupGitHookCmd())
   101
   102	return cmd
   103}
