   133// runEnableWithStrategy enables Entire with a specified strategy (non-interactive).
   134// The selectedStrategy can be either a display name (manual-commit, auto-commit)
   135// or an internal name (manual-commit, auto-commit).
   136func runEnableWithStrategy(w io.Writer, selectedStrategy string, localDev, _, useLocalSettings, useProjectSettings, forceHooks, setupShell, skipPushSessions, telemetry bool, disableMultisessionWarning bool) error {
   137	// Map the strategy to internal name if it's a display name
   138	internalStrategy := selectedStrategy
   139	if mapped, ok := strategyDisplayToInternal[selectedStrategy]; ok {
   140		internalStrategy = mapped
   141	}
   142
   143	// Validate the strategy exists
   144	strat, err := strategy.Get(internalStrategy)
   145	if err != nil {
   146		return fmt.Errorf("unknown strategy: %s (use manual-commit or auto-commit)", selectedStrategy)
   147	}
   148
   149	// Setup Claude Code hooks
   150	hooksInstalled, err := setupClaudeCodeHook(localDev, forceHooks)
   151	if err != nil {
   152		return fmt.Errorf("failed to setup Claude Code hooks: %w", err)
   153	}
   154	if hooksInstalled > 0 {
   155		fmt.Fprintln(w, " Claude Code hooks installed")
   156	} else {
   157		fmt.Fprintln(w, " Claude Code hooks verified")
   158	}
   159
   160	// Setup .entire directory
   161	dirCreated, err := setupEntireDirectory()
   162	if err != nil {
   163		return fmt.Errorf("failed to setup .entire directory: %w", err)
   164	}
   165	if dirCreated {
   166		fmt.Fprintln(w, " .entire directory created")
   167	}
   168
   169	// Load existing settings to preserve other options (like strategy_options.push)
   170	settings, err := LoadEntireSettings()
   171	if err != nil {
   172		// If we can't load, start with defaults
   173		settings = &EntireSettings{}
   174	}
   175	// Update the specific fields
   176	settings.Strategy = internalStrategy
   177	settings.LocalDev = localDev
   178	settings.Enabled = true
   179
   180	// Set push_sessions option if --skip-push-sessions flag was provided
   181	if skipPushSessions {
   182		if settings.StrategyOptions == nil {
   183			settings.StrategyOptions = make(map[string]interface{})
   184		}
   185		settings.StrategyOptions["push_sessions"] = false
   186	}
   187
   188	// Set disable_multisession_warning option if --disable-multisession-warning flag was provided
   189	if disableMultisessionWarning {
   190		if settings.StrategyOptions == nil {
   191			settings.StrategyOptions = make(map[string]interface{})
   192		}
   193		settings.StrategyOptions["disable_multisession_warning"] = true
   194	}
   195
   196	// Handle telemetry for non-interactive mode
   197	// Note: if telemetry is nil (not configured), it defaults to disabled
   198	if !telemetry || os.Getenv("ENTIRE_TELEMETRY_OPTOUT") != "" {
   199		f := false
   200		settings.Telemetry = &f
   201	}
   202
   203	// Determine which settings file to write to
   204	entireDirAbs, err := paths.AbsPath(paths.EntireDir)
   205	if err != nil {
   206		entireDirAbs = paths.EntireDir // Fallback to relative
   207	}
   208	shouldUseLocal, showNotification := determineSettingsTarget(entireDirAbs, useLocalSettings, useProjectSettings)
   209
   210	if showNotification {
   211		fmt.Fprintln(w, "Info: Project settings exist. Saving to settings.local.json instead.")
   212		fmt.Fprintln(w, "  Use --project to update the project settings file.")
   213	}
   214
   215	if shouldUseLocal {
   216		if err := SaveEntireSettingsLocal(settings); err != nil {
   217			return fmt.Errorf("failed to save local settings: %w", err)
   218		}
   219		fmt.Fprintln(w, " Local settings saved (.entire/settings.local.json)")
   220	} else {
   221		if err := SaveEntireSettings(settings); err != nil {
   222			return fmt.Errorf("failed to save settings: %w", err)
   223		}
   224		fmt.Fprintln(w, " Project settings saved (.entire/settings.json)")
   225	}
   226
   227	// Install git hooks (always reinstall to ensure they're up-to-date)
   228	gitHooksInstalled, err := strategy.InstallGitHook(true)
   229	if err != nil {
   230		return fmt.Errorf("failed to install git hooks: %w", err)
   231	}
   232	if gitHooksInstalled > 0 {
   233		fmt.Fprintln(w, " Git hooks installed")
   234	} else {
   235		fmt.Fprintln(w, " Git hooks verified")
   236	}
   237
   238	// Let the strategy handle its own setup requirements
   239	if err := strat.EnsureSetup(); err != nil {
   240		return fmt.Errorf("failed to setup strategy: %w", err)
   241	}
   242
   243	// Setup shell completion if --setup-shell flag was provided
   244	if setupShell {
   245		if err := setupShellCompletionNonInteractive(w); err != nil {
   246			// Non-fatal - just log and continue
   247			fmt.Fprintf(w, "Note: Shell completion setup skipped: %v\n", err)
   248		}
   249	}
   250
   251	// Show success message with display name
   252	displayName := selectedStrategy
   253	if dn, ok := strategyInternalToDisplay[internalStrategy]; ok {
   254		displayName = dn
   255	}
   256	fmt.Fprintf(w, "\n %s strategy enabled\n", displayName)
   257
   258	return nil
   259}
