   261// runEnableInteractive runs the interactive enable flow with strategy selection.
   262func runEnableInteractive(w io.Writer, localDev, _, useLocalSettings, useProjectSettings, forceHooks, setupShell, skipPushSessions, telemetry, disableMultisessionWarning bool) error {
   263	// Build strategy options with user-friendly names
   264	var selectedStrategy string
   265	options := []huh.Option[string]{
   266		huh.NewOption(strategyDisplayManualCommit+"  Sessions are only captured when you commit", strategyDisplayManualCommit),
   267		huh.NewOption(strategyDisplayAutoCommit+"  Automatically capture sessions after agent response completion", strategyDisplayAutoCommit),
   268	}
   269
   270	form := NewAccessibleForm(
   271		huh.NewGroup(
   272			huh.NewSelect[string]().
   273				Options(options...).
   274				Value(&selectedStrategy),
   275		),
   276	)
   277
   278	if err := form.Run(); err != nil {
   279		return fmt.Errorf("selection cancelled: %w", err)
   280	}
   281
   282	// Map display name to internal strategy name
   283	internalStrategy, ok := strategyDisplayToInternal[selectedStrategy]
   284	if !ok {
   285		return fmt.Errorf("unknown strategy: %s", selectedStrategy)
   286	}
   287
   288	// Setup Claude Code hooks
   289	hooksInstalled, err := setupClaudeCodeHook(localDev, forceHooks)
   290	if err != nil {
   291		return fmt.Errorf("failed to setup Claude Code hooks: %w", err)
   292	}
   293	if hooksInstalled > 0 {
   294		fmt.Fprintln(w, " Claude Code hooks installed")
   295	} else {
   296		fmt.Fprintln(w, " Claude Code hooks verified")
   297	}
   298
   299	// Setup .entire directory
   300	dirCreated, err := setupEntireDirectory()
   301	if err != nil {
   302		return fmt.Errorf("failed to setup .entire directory: %w", err)
   303	}
   304	if dirCreated {
   305		fmt.Fprintln(w, " .entire directory created")
   306	}
   307
   308	// Load existing settings to preserve other options (like strategy_options.push)
   309	settings, err := LoadEntireSettings()
   310	if err != nil {
   311		// If we can't load, start with defaults
   312		settings = &EntireSettings{}
   313	}
   314	// Update the specific fields
   315	settings.Strategy = internalStrategy
   316	settings.LocalDev = localDev
   317	settings.Enabled = true
   318
   319	// Set push_sessions option if --skip-push-sessions flag was provided
   320	if skipPushSessions {
   321		if settings.StrategyOptions == nil {
   322			settings.StrategyOptions = make(map[string]interface{})
   323		}
   324		settings.StrategyOptions["push_sessions"] = false
   325	}
   326
   327	// Set disable_multisession_warning option if --disable-multisession-warning flag was provided
   328	if disableMultisessionWarning {
   329		if settings.StrategyOptions == nil {
   330			settings.StrategyOptions = make(map[string]interface{})
   331		}
   332		settings.StrategyOptions["disable_multisession_warning"] = true
   333	}
   334
   335	// Ask about telemetry consent (only if not already asked)
   336	if err := promptTelemetryConsent(settings, telemetry); err != nil {
   337		return fmt.Errorf("telemetry consent: %w", err)
   338	}
   339
   340	// Determine which settings file to write to (interactive prompt if settings.json exists)
   341	entireDirAbs, err := paths.AbsPath(paths.EntireDir)
   342	if err != nil {
   343		entireDirAbs = paths.EntireDir // Fallback to relative
   344	}
   345	shouldUseLocal, err := promptSettingsTarget(entireDirAbs, useLocalSettings, useProjectSettings)
   346	if err != nil {
   347		return err
   348	}
   349
   350	if shouldUseLocal {
   351		if err := SaveEntireSettingsLocal(settings); err != nil {
   352			return fmt.Errorf("failed to save local settings: %w", err)
   353		}
   354		fmt.Fprintln(w, " Local settings saved (.entire/settings.local.json)")
   355	} else {
   356		if err := SaveEntireSettings(settings); err != nil {
   357			return fmt.Errorf("failed to save settings: %w", err)
   358		}
   359		fmt.Fprintln(w, " Project settings saved (.entire/settings.json)")
   360	}
   361
   362	// Install git hooks (always reinstall to ensure they're up-to-date)
   363	gitHooksInstalled, err := strategy.InstallGitHook(true)
   364	if err != nil {
   365		return fmt.Errorf("failed to install git hooks: %w", err)
   366	}
   367	if gitHooksInstalled > 0 {
   368		fmt.Fprintln(w, " Git hooks installed")
   369	} else {
   370		fmt.Fprintln(w, " Git hooks verified")
   371	}
   372
   373	// Let the strategy handle its own setup requirements
   374	strat, err := strategy.Get(internalStrategy)
   375	if err != nil {
   376		return fmt.Errorf("failed to get strategy: %w", err)
   377	}
   378	if err := strat.EnsureSetup(); err != nil {
   379		return fmt.Errorf("failed to setup strategy: %w", err)
   380	}
   381
   382	// Setup shell completion - either non-interactively (if --setup-shell) or prompt
   383	if setupShell {
   384		if err := setupShellCompletionNonInteractive(w); err != nil {
   385			// Non-fatal - just log and continue
   386			fmt.Fprintf(w, "Note: Shell completion setup skipped: %v\n", err)
   387		}
   388	} else {
   389		// Offer to setup shell completion (only if not already configured)
   390		if err := promptShellCompletion(w); err != nil {
   391			// Non-fatal - just log and continue
   392			fmt.Fprintf(w, "Note: Shell completion setup skipped: %v\n", err)
   393		}
   394	}
   395
   396	// Show success message with display name
   397	fmt.Fprintf(w, "\n %s strategy enabled\n", selectedStrategy)
   398
   399	return nil
   400}
