   402// runEnable is a simple enable that just sets the enabled flag (for programmatic use).
   403func runEnable(w io.Writer) error {
   404	settings, err := LoadEntireSettings()
   405	if err != nil {
   406		return fmt.Errorf("failed to load settings: %w", err)
   407	}
   408
   409	settings.Enabled = true
   410	if err := SaveEntireSettings(settings); err != nil {
   411		return fmt.Errorf("failed to save settings: %w", err)
   412	}
   413
   414	fmt.Fprintln(w, "Entire is now enabled.")
   415	return nil
   416}
