   418func runDisable(w io.Writer, useProjectSettings bool) error {
   419	settings, err := LoadEntireSettings()
   420	if err != nil {
   421		return fmt.Errorf("failed to load settings: %w", err)
   422	}
   423
   424	settings.Enabled = false
   425
   426	// If --project flag is specified, always write to project settings
   427	if useProjectSettings {
   428		if err := SaveEntireSettings(settings); err != nil {
   429			return fmt.Errorf("failed to save settings: %w", err)
   430		}
   431	} else {
   432		// Always write to local settings file (create if doesn't exist)
   433		if err := SaveEntireSettingsLocal(settings); err != nil {
   434			return fmt.Errorf("failed to save local settings: %w", err)
   435		}
   436	}
   437
   438	fmt.Fprintln(w, "Entire is now disabled.")
   439	return nil
   440}
