   442func runStatus(w io.Writer) error {
   443	// Check if we're in a git repository
   444	if _, repoErr := paths.RepoRoot(); repoErr != nil {
   445		fmt.Fprintln(w, " not a git repository")
   446		return nil //nolint:nilerr // Not being in a git repo is a valid status, not an error
   447	}
   448
   449	// Get absolute paths for settings files
   450	settingsPath, err := paths.AbsPath(EntireSettingsFile)
   451	if err != nil {
   452		settingsPath = EntireSettingsFile
   453	}
   454	localSettingsPath, err := paths.AbsPath(EntireSettingsLocalFile)
   455	if err != nil {
   456		localSettingsPath = EntireSettingsLocalFile
   457	}
   458
   459	// Check which settings files exist (for source label)
   460	_, projectErr := os.Stat(settingsPath)
   461	if projectErr != nil && !errors.Is(projectErr, fs.ErrNotExist) {
   462		return fmt.Errorf("cannot access project settings file: %w", projectErr)
   463	}
   464	_, localErr := os.Stat(localSettingsPath)
   465	if localErr != nil && !errors.Is(localErr, fs.ErrNotExist) {
   466		return fmt.Errorf("cannot access local settings file: %w", localErr)
   467	}
   468	projectExists := projectErr == nil
   469	localExists := localErr == nil
   470
   471	if !projectExists && !localExists {
   472		fmt.Fprintln(w, " not set up (run `entire enable` to get started)")
   473		return nil
   474	}
   475
   476	// Load merged settings
   477	settings, err := LoadEntireSettings()
   478	if err != nil {
   479		return fmt.Errorf("failed to load settings: %w", err)
   480	}
   481	settings.Strategy = strategy.NormalizeStrategyName(settings.Strategy)
   482
   483	// Determine source label
   484	var sourceLabel string
   485	switch {
   486	case projectExists && localExists:
   487		sourceLabel = "Project + Local"
   488	case localExists:
   489		sourceLabel = "Local"
   490	default:
   491		sourceLabel = "Project"
   492	}
   493
   494	fmt.Fprintln(w, formatSettingsStatus(sourceLabel, settings))
   495	return nil
   496}
