   498// formatSettingsStatus formats a settings status line.
   499// Output format: "Project, enabled (manual-commit)" or "Local, disabled (auto-commit)"
   500func formatSettingsStatus(prefix string, settings *EntireSettings) string {
   501	displayName := settings.Strategy
   502	if dn, ok := strategyInternalToDisplay[settings.Strategy]; ok {
   503		displayName = dn
   504	}
   505
   506	if settings.Enabled {
   507		return fmt.Sprintf("%s, enabled (%s)", prefix, displayName)
   508	}
   509	return fmt.Sprintf("%s, disabled (%s)", prefix, displayName)
   510}
