   512// DisabledMessage is the message shown when Entire is disabled
   513const DisabledMessage = "Entire is disabled. Run `entire enable` to re-enable."
   514
   515// checkDisabledGuard checks if Entire is disabled and prints a message if so.
   516// Returns true if the caller should exit (i.e., Entire is disabled).
   517// On error reading settings, defaults to enabled (returns false).
   518func checkDisabledGuard(w io.Writer) bool {
   519	enabled, err := IsEnabled()
   520	if err != nil {
   521		// Default to enabled on error
   522		return false
   523	}
   524	if !enabled {
   525		fmt.Fprintln(w, DisabledMessage)
   526		return true
   527	}
   528	return false
   529}
