   531// setupClaudeCodeHook sets up Claude Code hooks.
   532// This is a convenience wrapper that uses the agent package.
   533// Returns the number of hooks installed (0 if already installed).
   534func setupClaudeCodeHook(localDev, forceHooks bool) (int, error) {
   535	ag, err := agent.Get(agent.AgentNameClaudeCode)
   536	if err != nil {
   537		return 0, fmt.Errorf("failed to get claude-code agent: %w", err)
   538	}
   539
   540	hookAgent, ok := ag.(agent.HookSupport)
   541	if !ok {
   542		return 0, errors.New("claude-code agent does not support hooks")
   543	}
   544
   545	count, err := hookAgent.InstallHooks(localDev, forceHooks)
   546	if err != nil {
   547		return 0, fmt.Errorf("failed to install claude-code hooks: %w", err)
   548	}
   549
   550	return count, nil
   551}
