   553// setupAgentHooksNonInteractive sets up hooks for a specific agent non-interactively.
   554// If strategyName is provided, it sets the strategy; otherwise uses default.
   555func setupAgentHooksNonInteractive(agentName agent.AgentName, strategyName string, localDev, forceHooks, skipPushSessions, telemetry, disableMultisessionWarning bool) error {
   556	ag, err := agent.Get(agentName)
   557	if err != nil {
   558		return fmt.Errorf("unknown agent: %s", agentName)
   559	}
   560
   561	// Check if agent supports hooks
   562	hookAgent, ok := ag.(agent.HookSupport)
   563	if !ok {
   564		return fmt.Errorf("agent %s does not support hooks", agentName)
   565	}
   566
   567	// Install hooks
   568	count, err := hookAgent.InstallHooks(localDev, forceHooks)
   569	if err != nil {
   570		return fmt.Errorf("failed to install hooks for %s: %w", agentName, err)
   571	}
   572
   573	if count == 0 {
   574		msg := fmt.Sprintf("Hooks for %s already installed", ag.Description())
   575		if agentName == agent.AgentNameGemini {
   576			msg += " - This is a work in progress"
   577		}
   578		fmt.Println(msg)
   579	} else {
   580		msg := fmt.Sprintf("Installed %d hooks for %s", count, ag.Description())
   581		if agentName == agent.AgentNameGemini {
   582			msg += " - This is a work in progress"
   583		}
   584		fmt.Println(msg)
   585	}
   586
   587	// Update settings to store the agent choice and strategy
   588	settings, _ := LoadEntireSettings() //nolint:errcheck // settings defaults are fine
   589	settings.Agent = string(agentName)
   590	settings.Enabled = true
   591	if localDev {
   592		settings.LocalDev = localDev
   593	}
   594
   595	// Set push_sessions option if --skip-push-sessions flag was provided
   596	if skipPushSessions {
   597		if settings.StrategyOptions == nil {
   598			settings.StrategyOptions = make(map[string]interface{})
   599		}
   600		settings.StrategyOptions["push_sessions"] = false
   601	}
   602
   603	// Set disable_multisession_warning option if --disable-multisession-warning flag was provided
   604	if disableMultisessionWarning {
   605		if settings.StrategyOptions == nil {
   606			settings.StrategyOptions = make(map[string]interface{})
   607		}
   608		settings.StrategyOptions["disable_multisession_warning"] = true
   609	}
   610
   611	// Set strategy if provided
   612	if strategyName != "" {
   613		// Map display name to internal name if needed
   614		internalStrategy := strategyName
   615		if mapped, ok := strategyDisplayToInternal[strategyName]; ok {
   616			internalStrategy = mapped
   617		}
   618		// Validate the strategy exists
   619		if _, err := strategy.Get(internalStrategy); err != nil {
   620			return fmt.Errorf("unknown strategy: %s (use manual-commit or auto-commit)", strategyName)
   621		}
   622		settings.Strategy = internalStrategy
   623	}
   624
   625	// Handle telemetry for non-interactive mode
   626	// Note: if telemetry is nil (not configured), it defaults to disabled
   627	if !telemetry || os.Getenv("ENTIRE_TELEMETRY_OPTOUT") != "" {
   628		f := false
   629		settings.Telemetry = &f
   630	}
   631
   632	if err := SaveEntireSettings(settings); err != nil {
   633		return fmt.Errorf("failed to save settings: %w", err)
   634	}
   635
   636	// Install git hooks (always reinstall to ensure they're up-to-date)
   637	if _, err := strategy.InstallGitHook(true); err != nil {
   638		return fmt.Errorf("failed to install git hooks: %w", err)
   639	}
   640
   641	// Let the strategy handle its own setup requirements (creates entire/sessions branch, etc.)
   642	strat, err := strategy.Get(settings.Strategy)
   643	if err != nil {
   644		return fmt.Errorf("failed to get strategy: %w", err)
   645	}
   646	if err := strat.EnsureSetup(); err != nil {
   647		return fmt.Errorf("failed to setup strategy: %w", err)
   648	}
   649
   650	return nil
   651}
   652
   653// validateSetupFlags checks that --local and --project flags are not both specified.
   654func validateSetupFlags(useLocal, useProject bool) error {
   655	if useLocal && useProject {
   656		return errors.New("cannot specify both --project and --local")
   657	}
   658	return nil
   659}
   660
   661// determineSettingsTarget decides whether to write to settings.local.json based on:
   662// - Whether settings.json already exists
   663// - The --local and --project flags
   664// Returns (useLocal, showNotification).
   665func determineSettingsTarget(entireDir string, useLocal, useProject bool) (bool, bool) {
   666	// Explicit --local flag always uses local settings
   667	if useLocal {
   668		return true, false
   669	}
   670
   671	// Explicit --project flag always uses project settings
   672	if useProject {
   673		return false, false
   674	}
   675
   676	// No flags specified - check if settings file exists
   677	settingsPath := filepath.Join(entireDir, paths.SettingsFileName)
   678	if _, err := os.Stat(settingsPath); err == nil {
   679		// Settings file exists - auto-redirect to local with notification
   680		return true, true
   681	}
   682
   683	// Settings file doesn't exist - create it
   684	return false, false
   685}
   686
   687// Settings target options for interactive prompt
   688const (
   689	settingsTargetProject = "project"
   690	settingsTargetLocal   = "local"
   691)
   692
   693// promptSettingsTarget interactively asks the user where to save settings
   694// when settings.json already exists and no flags were provided.
   695// Returns (useLocal, error).
   696func promptSettingsTarget(entireDir string, useLocal, useProject bool) (bool, error) {
   697	// Explicit --local flag always uses local settings
   698	if useLocal {
   699		return true, nil
   700	}
   701
   702	// Explicit --project flag always uses project settings
   703	if useProject {
   704		return false, nil
   705	}
   706
   707	// Check if settings file exists
   708	settingsPath := filepath.Join(entireDir, paths.SettingsFileName)
   709	if _, err := os.Stat(settingsPath); os.IsNotExist(err) {
   710		// Settings file doesn't exist - create it (no prompt needed)
   711		return false, nil
   712	}
   713
   714	// Settings file exists - prompt user
   715	var selected string
   716	form := NewAccessibleForm(
   717		huh.NewGroup(
   718			huh.NewSelect[string]().
   719				Title("Project settings already exist. Where should settings be saved?").
   720				Options(
   721					huh.NewOption("Update project settings (settings.json)", settingsTargetProject),
   722					huh.NewOption("Use local settings (settings.local.json, gitignored)", settingsTargetLocal),
   723				).
   724				Value(&selected),
   725		),
   726	)
   727
   728	if err := form.Run(); err != nil {
   729		return false, fmt.Errorf("selection cancelled: %w", err)
   730	}
   731
   732	return selected == settingsTargetLocal, nil
   733}
   734
   735// setupEntireDirectory creates the .entire directory and gitignore.
   736// Returns true if the directory was created, false if it already existed.
   737func setupEntireDirectory() (bool, error) {
   738	// Get absolute path for the .entire directory
   739	entireDirAbs, err := paths.AbsPath(paths.EntireDir)
   740	if err != nil {
   741		entireDirAbs = paths.EntireDir // Fallback to relative
   742	}
   743
   744	// Check if directory already exists
   745	created := false
   746	if _, err := os.Stat(entireDirAbs); os.IsNotExist(err) {
   747		created = true
   748	}
   749
   750	// Create .entire directory
   751	//nolint:gosec // G301: Project directory needs standard permissions for git
   752	if err := os.MkdirAll(entireDirAbs, 0o755); err != nil {
   753		return false, fmt.Errorf("failed to create .entire directory: %w", err)
   754	}
   755
   756	// Create/update .gitignore with all required entries
   757	if err := strategy.EnsureEntireGitignore(); err != nil {
   758		return false, fmt.Errorf("failed to setup .gitignore: %w", err)
   759	}
   760
   761	return created, nil
   762}
   763
   764// setupGitHook installs the prepare-commit-msg hook for context trailers.
   765func setupGitHook() error {
   766	// Use shared implementation from strategy package
   767	// The localDev setting is read from settings.json
   768	_, err := strategy.InstallGitHook(false) // not silent - show output during setup
   769	if err != nil {
   770		return fmt.Errorf("failed to install git hook: %w", err)
   771	}
   772	return nil
   773}
   774
   775// newSetupGitHookCmd creates the standalone git-hook setup command
   776func newSetupGitHookCmd() *cobra.Command {
   777	cmd := &cobra.Command{
   778		Use:    "git-hook",
   779		Short:  "Install git hook for session context trailers",
   780		Hidden: true, // Hidden as it's mainly for testing
   781		RunE: func(_ *cobra.Command, _ []string) error {
   782			return setupGitHook()
   783		},
   784	}
   785
   786	return cmd
   787}
   788
   789// shellCompletionComment is the comment preceding the completion line
   790const shellCompletionComment = "# Entire CLI shell completion"
   791
   792// promptShellCompletion offers to add shell completion to the user's rc file.
   793// Only prompts if completion is not already configured.
   794func promptShellCompletion(w io.Writer) error {
   795	// Get user's home directory
   796	home, err := os.UserHomeDir()
   797	if err != nil {
   798		//nolint:nilerr // Skip silently if we can't determine home - not a fatal error
   799		return nil
   800	}
   801
   802	// Determine shell and rc file
   803	shell := os.Getenv("SHELL")
   804	var rcFile string
   805	var completionLine string
   806
   807	switch {
   808	case strings.Contains(shell, "zsh"):
   809		rcFile = filepath.Join(home, ".zshrc")
   810		completionLine = "source <(entire completion zsh)"
   811	case strings.Contains(shell, "bash"):
   812		rcFile = filepath.Join(home, ".bashrc")
   813		completionLine = "source <(entire completion bash)"
   814	default:
   815		return nil // Unsupported shell, skip silently
   816	}
   817
   818	// Check if completion is already configured
   819	if isCompletionConfigured(rcFile) {
   820		return nil // Already configured, skip silently
   821	}
   822
   823	// Prompt user with select-style picker (matching other prompts)
   824	var selected string
   825	form := NewAccessibleForm(
   826		huh.NewGroup(
   827			huh.NewSelect[string]().
   828				Title("Enable shell completion?").
   829				Options(
   830					huh.NewOption("Yes", "yes"),
   831					huh.NewOption("No", "no"),
   832				).
   833				Value(&selected),
   834		),
   835	)
   836
   837	if err := form.Run(); err != nil {
   838		//nolint:nilerr // User cancelled - not a fatal error, just skip
   839		return nil
   840	}
   841
   842	if selected != "yes" {
   843		return nil
   844	}
   845
   846	// Append completion to rc file
   847	if err := appendShellCompletion(rcFile, completionLine); err != nil {
   848		return fmt.Errorf("failed to update %s: %w", rcFile, err)
   849	}
   850
   851	fmt.Fprintf(w, " Shell completion added to %s\n", rcFile)
   852	fmt.Fprintln(w, "  Run `source "+rcFile+"` or restart your shell to activate")
   853
   854	return nil
   855}
   856
   857// isCompletionConfigured checks if shell completion is already in the rc file.
   858func isCompletionConfigured(rcFile string) bool {
   859	//nolint:gosec // G304: rcFile is constructed from home dir + known filename, not user input
   860	content, err := os.ReadFile(rcFile)
   861	if err != nil {
   862		return false // File doesn't exist or can't read, treat as not configured
   863	}
   864	return strings.Contains(string(content), "entire completion")
   865}
   866
   867// appendShellCompletion adds the completion line to the rc file.
   868func appendShellCompletion(rcFile, completionLine string) error {
   869	//nolint:gosec // G302: Shell rc files need 0644 for user readability
   870	f, err := os.OpenFile(rcFile, os.O_APPEND|os.O_CREATE|os.O_WRONLY, 0o644)
   871	if err != nil {
   872		return fmt.Errorf("opening file: %w", err)
   873	}
   874	defer f.Close()
   875
   876	// Add newline, comment, and completion line
   877	_, err = f.WriteString("\n" + shellCompletionComment + "\n" + completionLine + "\n")
   878	if err != nil {
   879		return fmt.Errorf("writing completion: %w", err)
   880	}
   881	return nil
   882}
   883
   884// setupShellCompletionNonInteractive adds shell completion without prompting.
   885// Used when --setup-shell flag is provided.
   886func setupShellCompletionNonInteractive(w io.Writer) error {
   887	// Get user's home directory
   888	home, err := os.UserHomeDir()
   889	if err != nil {
   890		return fmt.Errorf("cannot determine home directory: %w", err)
   891	}
   892
   893	// Determine shell and rc file
   894	shell := os.Getenv("SHELL")
   895	var rcFile string
   896	var completionLine string
   897
   898	switch {
   899	case strings.Contains(shell, "zsh"):
   900		rcFile = filepath.Join(home, ".zshrc")
   901		completionLine = "source <(entire completion zsh)"
   902	case strings.Contains(shell, "bash"):
   903		rcFile = filepath.Join(home, ".bashrc")
   904		completionLine = "source <(entire completion bash)"
   905	default:
   906		return fmt.Errorf("unsupported shell: %s (supported: zsh, bash)", shell)
   907	}
   908
   909	// Check if completion is already configured
   910	if isCompletionConfigured(rcFile) {
   911		fmt.Fprintf(w, " Shell completion already configured in %s\n", rcFile)
   912		return nil
   913	}
   914
   915	// Append completion to rc file
   916	if err := appendShellCompletion(rcFile, completionLine); err != nil {
   917		return fmt.Errorf("failed to update %s: %w", rcFile, err)
   918	}
   919
   920	fmt.Fprintf(w, " Shell completion added to %s\n", rcFile)
   921	fmt.Fprintln(w, "  Run `source "+rcFile+"` or restart your shell to activate")
   922
   923	return nil
   924}
   925
   926// promptTelemetryConsent asks the user if they want to enable telemetry.
   927// It modifies settings.Telemetry based on the user's choice or flags.
   928// The caller is responsible for saving settings.
   929func promptTelemetryConsent(settings *EntireSettings, telemetryFlag bool) error {
   930	// Handle --telemetry=false flag first (always overrides existing setting)
   931	if !telemetryFlag {
   932		f := false
   933		settings.Telemetry = &f
   934		return nil
   935	}
   936
   937	// Skip if already asked
   938	if settings.Telemetry != nil {
   939		return nil
   940	}
   941
   942	// Skip if env var disables telemetry (record as disabled)
   943	if os.Getenv("ENTIRE_TELEMETRY_OPTOUT") != "" {
   944		f := false
   945		settings.Telemetry = &f
   946		return nil
   947	}
   948
   949	consent := true // Default to Yes
   950	form := NewAccessibleForm(
   951		huh.NewGroup(
   952			huh.NewConfirm().
   953				Title("Help improve Entire CLI?").
   954				Description("Share anonymous usage data. No code or personal info collected.").
   955				Affirmative("Yes").
   956				Negative("No").
   957				Value(&consent),
   958		),
   959	)
   960
   961	if err := form.Run(); err != nil {
   962		return fmt.Errorf("telemetry prompt: %w", err)
   963	}
   964
   965	settings.Telemetry = &consent
   966	return nil
   967}
   968

