   282	settings, err := LoadEntireSettings()
   283	if err != nil {
   284		// Fall back to default on error
   285		logging.Info(context.Background(), "falling back to default strategy - failed to load settings",
   286			slog.String("error", err.Error()))
   287		return strategy.Default()
   288	}
   289
   290	s, err := strategy.Get(settings.Strategy)
   291	if err != nil {
   292		// Fall back to default if strategy not found
   293		logging.Info(context.Background(), "falling back to default strategy - configured strategy not found",
   294			slog.String("configured", settings.Strategy),
   295			slog.String("error", err.Error()))
   296		return strategy.Default()
   297	}
   298
   299	return s
