   361// Returns empty string if not configured (caller should use default).
   362// Note: ENTIRE_LOG_LEVEL env var takes precedence; check it first.
   363func GetLogLevel() string {
   364	settings, err := LoadEntireSettings()
   365	if err != nil {
   366		return ""
   367	}
   368	return settings.LogLevel
   369}
   370
   371// IsMultiSessionWarningDisabled checks if multi-session warnings are disabled.
   372// Returns false (show warnings) by default if settings cannot be loaded or the key is missing.
   373func IsMultiSessionWarningDisabled() bool {
   374	settings, err := LoadEntireSettings()
   375	if err != nil {
   376		return false // Default: show warnings
   377	}
   378	if settings.StrategyOptions == nil {
   379		return false
   380	}
   381	if disabled, ok := settings.StrategyOptions["disable_multisession_warning"].(bool); ok {
   382		return disabled
   383	}
   384	return false
   385}
